import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function workoutReducer(state = initialState.workouts, action) {
  switch (action.type) {
    case actionTypes.ADD_WORKOUT:
      const workoutId = uuidv4();
      const workoutTitle =
        action.payload.name || `Workout ${Object.keys(state).length + 1}`;
      const newWorkout = {
        id: workoutId,
        name: workoutTitle,
        exercises: action.payload.exercises || [],
        programId: action.payload.programId,
        order: Object.keys(state).length + 1
      };
      return {
        ...state,
        [workoutId]: newWorkout
      };

    case actionTypes.SET_WORKOUTS:
      return {
        ...state,
        ...action.payload.reduce((acc, workout) => {
          acc[workout.id] = workout;
          return acc;
        }, {})
      };

    case actionTypes.UPDATE_WORKOUT:
      const updatedWorkout = {
        ...state[action.payload.id],
        ...action.payload
      };
      return {
        ...state,
        [action.payload.id]: updatedWorkout
      };

    case actionTypes.DELETE_WORKOUT:
      const { [action.payload]: _, ...remainingWorkouts } = state;
      return remainingWorkouts;

    default:
      return state;
  }
}

export { workoutReducer };
