import { v4 as uuidv4 } from 'uuid';

export const standardSetStructure = {
  id: null, // Database-assigned ID for existing sets
  tempId: null, // Temporary ID for new sets (client-side only)
  reps: 0,
  weight: 0,
  order: 0,
  isNew: false // Flag to indicate if this is a newly added set
};

export const standardExerciseStructure = {
  id: null, // Database-assigned ID for existing exercises
  tempId: null, // Temporary ID for new exercises (client-side only)
  name: '',
  muscle: '',
  equipment: '',
  catalog_exercise_id: null, // ID from the exercise catalog
  order: 0,
  sets: [], // Array of set objects
  isNew: false // Flag to indicate if this is a newly added exercise
};

export const standardWorkoutStructure = {
  id: null, // Database-assigned ID for existing workouts
  tempId: null, // Temporary ID for new workouts (client-side only)
  name: '',
  order: 0,
  exercises: [], // Array of exercise objects
  isNew: false // Flag to indicate if this is a newly added workout
};

export const standardProgramStructure = {
  id: null,
  name: '',
  program_duration: 0,
  days_per_week: 0,
  main_goal: '',
  duration_unit: '',
  user_id: null,
  workouts: {} // Object with workout IDs as keys
};

export const createNewProgram = () => ({
  ...standardProgramStructure,
  id: uuidv4()
});

export const createNewWorkout = () => ({
  ...standardWorkoutStructure,
  tempId: uuidv4(),
  isNew: true
});

export const createNewExercise = (catalogExercise = null) => {
  const newExercise = {
    ...standardExerciseStructure,
    tempId: uuidv4(),
    isNew: true
  };

  if (catalogExercise) {
    return {
      ...newExercise,
      catalog_exercise_id: catalogExercise.id,
      name: catalogExercise.name,
      muscle: catalogExercise.muscle,
      equipment: catalogExercise.equipment
    };
  }

  return newExercise;
};

export const createNewSet = () => ({
  ...standardSetStructure,
  tempId: uuidv4(),
  isNew: true
});
