import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import TextInput from '../../../components/Inputs/TextInput';
import { BsChevronCompactUp, BsChevronCompactDown } from 'react-icons/bs';
import Button from '../../../components/Inputs/Button';
import NavBar from '../../../components/Nav/Nav';
import { ProgramContext } from '../../../contexts/programContext';
import { useTheme } from '../../../contexts/themeContext';
import { DURATION_TYPES, GOAL_TYPES } from '../../../utils/constants';
import { toProperCase } from '../../../utils/stringUtils';
import './programs.css';

const ProgramDetailsPage = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [localPrograms, setLocalPrograms] = useState([]);

  const { state, deleteProgram } = useContext(ProgramContext);
  const { theme } = useTheme();

  const handleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const fetchPrograms = async () => {
    try {
      const response = await fetch('http://localhost:9025/api/programs/2');
      const data = await response.json();
      setLocalPrograms(data || []);
    } catch (error) {
      console.error('Error fetching programs:', error);
    }
  };

  useEffect(() => {
    // Fetch all programs initially
    fetchPrograms();
  }, []);

  useEffect(() => {
    if (state.programs) {
      setLocalPrograms(Object.values(state.programs));
    }
  }, [state.programs]);

  const handleDeleteProgram = async programId => {
    await deleteProgram(programId);
    // Re-fetch programs after deletion
    fetchPrograms();
  };

  return (
    <div>
      <NavBar />
      <div className='view-prog-page'>
        <div className='view-prog-page__page-title-container'>
          <h1 className='view-prog-page__page-title'>Program Details</h1>
          <Link
            className={`view-prog-page__title-link ${theme}`}
            to='/programs'
          >
            Back to Programs
          </Link>
        </div>
        <div className={`view-prog-page__search-container ${theme}`}>
          <div className='view-prog-page__search-inputs'>
            <div className={`view-prog-page__search-input-top-row ${theme}`}>
              <TextInput
                list='programs'
                className={`program-search__search-text-input ${theme}`}
                id='program-search-bar'
                type='search'
                placeholder='Program Names'
              />
              <datalist id='programs'>
                {localPrograms.map((program, index) => (
                  <option key={index} value={program.name} />
                ))}
              </datalist>
              <select className={`program-search__goals ${theme}`}>
                <option value=''>Goal</option>
                {GOAL_TYPES.map((option, index) => (
                  <option key={index} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
            <div className={`view-prog-page__search-input-bottom-row ${theme}`}>
              <TextInput
                className={`program-search__duration ${theme}`}
                type='search'
                placeholder='Duration'
              />
              <select className={`program-search__duration-unit ${theme}`}>
                <option value=''>Duration Type</option>
                {DURATION_TYPES.map((option, index) => (
                  <option key={index} value={option.label}>
                    {option.label}
                  </option>
                ))}
              </select>
              <TextInput
                className={`program-search__days-per-week ${theme}`}
                type='search'
                placeholder='Days Per Week'
              />
            </div>

            <div
              className={`view-prog-page__search-input-container ${theme}`}
            ></div>
          </div>
        </div>
      </div>
      <div className='view-prog-page__program-list'>
        <div key={program.id} className={`view-prog-page__program ${theme}`}>
          <h2 className='view-prog-page__program-title'>{program.name}</h2>
          <div className='view-prog-page__program-details'>
            <div className='view-prog-page__program-details-section'>
              <p className='view-prog-page__program-details-label'>Main Goal</p>
              <p className='view-prog-page__program-details-value'>
                {toProperCase(program.main_goal)}
              </p>
            </div>
            <div className='view-prog-page__program-details-section'>
              <p className='view-prog-page__program-details-label'>Duration</p>
              <p className='view-prog-page__program-details-value'>
                {program.program_duration} {toProperCase(program.duration_unit)}
              </p>
            </div>
            <div className='view-prog-page__program-details-section'>
              <p className='view-prog-page__program-details-label'>
                Days Per Week
              </p>
              <p className='view-prog-page__program-details-value'>
                {program.days_per_week}
              </p>
            </div>
          </div>
        </div>
      </div>
      <div className='create-prog-page__button-container'>
        <Button type='button' onClick={handleEditProgram}>
          Edit
        </Button>
        <Button type='submit' onClick={handleDeleteProgram}>
          Delete
        </Button>
      </div>
    </div>
  );
};

export default ProgramDetailsPage;
