import { createContext, useReducer, useEffect } from 'react';
import { actionTypes } from '../actions/actionTypes';
import rootReducer from '../reducers/rootReducer';
import { initialState } from '../reducers/initialState';
import { v4 as uuidv4 } from 'uuid';

export const ProgramContext = createContext();

export const ProgramProvider = ({ children }) => {
  const [state, dispatch] = useReducer(rootReducer, initialState);

  useEffect(() => {
    console.log('State updated:', state);
  }, [state]);

  const setActiveWorkout = (workoutId, programId) => {
    if (!workoutId || !programId) {
      console.error('Attempted to set active workout without a valid IDs');
      return;
    }
    console.log(
      'Setting active workout:',
      workoutId,
      'for program:',
      programId
    );
    dispatch({
      type: actionTypes.SET_ACTIVE_WORKOUT,
      payload: { workoutId, programId }
    });
  };

  const saveProgram = async () => {
    const programId = Object.keys(state.programs)[0];
    const newProgram = {
      ...state.programs[programId],
      workouts: Object.values(state.workouts).map(workout => ({
        ...workout,
        exercises: (state.exercises[workout.id] || []).map(exercise => ({
          ...exercise,
          sets: state.sets[exercise.id] || []
        })),
        order: workout.order || 1
      }))
    };

    dispatch({ type: actionTypes.SAVE_PROGRAM_START });
    try {
      // console.log('Saving program:', newProgram);
      validateProgramData(newProgram); // Validate data before sending
      const response = await fetch('http://localhost:9025/api/programs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newProgram)
      });

      if (!response.ok) {
        const errorText = await response.text(); // Get the response text
        console.error('Error saving program:', errorText); // Log the error text
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();
      dispatch({
        type: actionTypes.SAVE_PROGRAM_SUCCESS,
        payload: savedProgram
      });
    } catch (error) {
      console.error('Failed to save program:', error);
      dispatch({
        type: actionTypes.SAVE_PROGRAM_FAILURE,
        payload: error.message
      });
    }
  };

  const updateProgram = async programId => {
    const updatedProgram = {
      ...state.programs[programId],
      workouts: Object.values(state.workouts)
        .map(workout => {
          if (
            !workout.id ||
            (typeof workout.id !== 'number' && workout.id.includes('-'))
          ) {
            return null; // Exclude invalid or temporary workouts
          }

          const updatedExercises = (state.exercises[workout.id] || [])
            .map(exercise => {
              if (
                !exercise.id ||
                (typeof exercise.id !== 'number' && exercise.id.includes('-'))
              ) {
                return null; // Exclude invalid or temporary exercises
              }

              return {
                ...exercise,
                sets: state.sets[exercise.id] || [],
                id: exercise.id
              };
            })
            .filter(exercise => exercise !== null); // Remove null exercises

          return {
            ...workout,
            exercises: updatedExercises,
            id: workout.id,
            programId: programId
          };
        })
        .filter(workout => workout !== null), // Remove null workouts
      id: programId
    };

    console.log('Original Program:', state.programs[programId]);
    console.log('Updated Program Data being sent to API:', updatedProgram);

    dispatch({ type: actionTypes.SAVE_PROGRAM_START });
    try {
      validateProgramData(updatedProgram); // Validate data before sending
      const response = await fetch(
        `http://localhost:9025/api/programs/${programId}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(updatedProgram)
        }
      );

      if (!response.ok) {
        const errorText = await response.text(); // Get the response text
        console.error('Error updating program:', errorText); // Log the error text
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();
      console.log(
        'Dispatching UPDATE_PROGRAM_SUCCESS with payload:',
        savedProgram
      );
      dispatch({
        type: actionTypes.UPDATE_PROGRAM_SUCCESS,
        payload: {
          ...savedProgram,
          id: programId
        }
      });
    } catch (error) {
      console.error('Failed to update program:', error);
      dispatch({
        type: actionTypes.SAVE_PROGRAM_FAILURE,
        payload: error.message
      });
    }
  };

  const validateProgramData = programData => {
    if (!programData.workouts || !Array.isArray(programData.workouts)) {
      throw new Error('Workouts should be an array.');
    }
    programData.workouts.forEach(workout => {
      if (!workout.exercises || !Array.isArray(workout.exercises)) {
        throw new Error('Exercises should be an array.');
      }
      workout.exercises.forEach(exercise => {
        if (!exercise.sets || !Array.isArray(exercise.sets)) {
          throw new Error('Sets should be an array.');
        }
      });
    });
  };

  const addProgram = details => {
    dispatch({
      type: actionTypes.ADD_PROGRAM,
      payload: details
    });
  };

  const deleteProgram = async programId => {
    try {
      // console.log('Deleting program:', programId);

      const response = await fetch(
        `http://localhost:9025/api/programs/${programId}`,
        {
          method: 'DELETE'
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error deleting program:', errorText);
        throw new Error('Failed to delete program');
      }

      // console.log('Dispatching DELETE_PROGRAM for program ID:', programId);
      dispatch({
        type: actionTypes.DELETE_PROGRAM,
        payload: { programId }
      });
    } catch (error) {
      console.error('Failed to delete program:', error);
    }
  };

  const addWorkout = workout => {
    dispatch({
      type: actionTypes.ADD_WORKOUT,
      payload: workout
    });
  };

  const updateWorkout = workout => {
    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: workout
    });
  };

  const deleteWorkout = workoutId => {
    dispatch({
      type: actionTypes.DELETE_WORKOUT,
      payload: workoutId
    });
  };

  console.log('State before adding exercise:', state);
  const addExercise = (workoutId, exercises) => {
    console.log('State before adding exercise:', state);
    const normalizedExercises = exercises.map(exercise => {
      return {
        id: exercise.id || uuidv4(),
        workout_id: exercise.workout_id || workoutId,
        catalog_exercise_id: exercise.catalog_exercise_id || exercise.id,
        exercise_name: exercise.exercise_name || exercise.name,
        muscle: exercise.muscle,
        equipment: exercise.equipment,
        file_path: exercise.file_path,
        order: exercise.order || 1 // You might want to handle the order properly
      };
    });
    dispatch({
      type: actionTypes.ADD_EXERCISE,
      payload: { workoutId, exercises: normalizedExercises }
    });
  };

  const updateExercise = (workoutId, exercise) => {
    const normalizedExercise = {
      ...exercise,
      name: exercise.name || exercise.exercise_name // Normalize the name property
    };
    dispatch({
      type: actionTypes.UPDATE_EXERCISE,
      payload: { workoutId, exercise: normalizedExercise }
    });
  };

  const deleteExercise = (workoutId, exerciseId) => {
    dispatch({
      type: actionTypes.DELETE_EXERCISE,
      payload: { workoutId, exerciseId }
    });
  };

  const addSet = (workoutId, exerciseId, weight = 10, reps = 10) => {
    console.log('Adding set for workout:', workoutId, 'exercise:', exerciseId);

    // Check if the workout exists
    if (!state.workouts[workoutId]) {
      console.error('Workout not found:', workoutId);
      return;
    }

    // Check if the exercise exists
    const exerciseExists = state.exercises[workoutId]?.some(
      ex => ex.id === exerciseId
    );
    if (!exerciseExists) {
      console.error(
        'Exercise not found:',
        exerciseId,
        'in workout:',
        workoutId
      );
      return;
    }

    dispatch({
      type: actionTypes.ADD_SET,
      payload: { workoutId, exerciseId, weight, reps }
    });
  };

  const updateSet = (workoutId, exerciseId, updatedSet) => {
    // console.log('Dispatching UPDATE_SET:', {
    //   workoutId,
    //   exerciseId,
    //   updatedSet
    // });
    dispatch({
      type: actionTypes.UPDATE_SET,
      payload: { workoutId, exerciseId, updatedSet }
    });
  };

  const deleteSet = (workoutId, exerciseId, setId) => {
    const exercise = state.exercises[workoutId].find(
      ex => ex.id === exerciseId
    );
    const initialState = exercise?.sets || [];
    const additionalSets = state.sets[exerciseId] || [];
    const combinedSets = [...initialState, ...additionalSets];
    // console.log('combinedSets:', combinedSets);

    // const exerciseSets = state.sets[exerciseId];
    // console.log('exerciseSets in deleteSet:', exerciseSets);
    // console.log('combinedSets length:', combinedSets.length);

    if (combinedSets.length > 1) {
      // console.log('Dispatching DELETE_SET:', { workoutId, exerciseId, setId });
      dispatch({
        type: actionTypes.DELETE_SET,
        payload: { workoutId, exerciseId, setId }
      });
    } else {
      console.log('Cannot delete the only remaining set.');
    }
  };

  const clearState = () => {
    dispatch({ type: actionTypes.CLEAR_STATE });
  };

  const loadProgramData = programData => {
    dispatch({ type: actionTypes.SET_PROGRAM, payload: programData });
    dispatch({ type: actionTypes.SET_WORKOUTS, payload: programData.workouts });
  };

  return (
    <ProgramContext.Provider
      value={{
        state,
        dispatch,
        activeWorkout: state.activeWorkout,
        addProgram,
        updateProgram,
        deleteProgram,
        loadProgramData,
        addWorkout,
        updateWorkout,
        deleteWorkout,
        setActiveWorkout,
        addExercise,
        updateExercise,
        deleteExercise,
        addSet,
        updateSet,
        deleteSet,
        saveProgram,
        clearState
      }}
    >
      {children}
    </ProgramContext.Provider>
  );
};
