import { initialState } from './initialState';

function programReducer(state, action) {
  switch (action.type) {
    case 'SET_SELECTED_PROGRAM':
      return {
        ...state,
        selected: true
      };

    case 'ADD_PROGRAM':
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      if (!state[id]) {
        console.error('Program not found:', id);
        return state; // Return the current state if the program ID does not exist.
      }

      const updatedState = {
        ...state,
        [id]: {
          ...state[id], // Spread the existing program details
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };

      return updatedState;

    case 'UPDATE_PROGRAM':
      console.log('UPDATE_PROGRAM action received');
      console.log('Current state:', state);
      console.log('Action payload:', action.payload);
      return {
        ...state,
        program: action.payload
      };
    case 'SET_SELECTED_WORKOUT_ID':
      return {
        ...state,
        selectedWorkoutId: action.payload
      };

    case 'UPDATE_PROGRAM_SUCCESS': {
      const updatedProgram = action.payload;

      if (!updatedProgram || !updatedProgram.id) {
        console.error('Invalid program data received:', updatedProgram);
        return state;
      }

      const updatedState = {
        ...state,
        [updatedProgram.id]: updatedProgram,
        selectedProgram: updatedProgram
      };

      return updatedState;
    }

    case 'UPDATE_PROGRAM_WORKOUTS':
      return {
        ...state,
        workouts: state.workouts.map(w =>
          w.id === action.payload.workout.id ? action.payload.workout : w
        )
      };

    case 'DELETE_PROGRAM': {
      const { programId } = action.payload;

      if (!programId) {
        console.error('Invalid payload for DELETE_PROGRAM', action.payload);
        return state;
      }

      const newPrograms = { ...state.programs };
      delete newPrograms[programId];

      const newState = {
        ...state,
        programs: newPrograms
      };

      return newState;
    }

    default:
      return state;
  }
}

export { programReducer };
