import { v4 as uuidv4 } from 'uuid';

// Static IDs for initial state
const initialProgramId = uuidv4();
const initialWorkoutId = uuidv4();

export const initialState = {
  programs: {
    [initialProgramId]: {
      user_id: 2,
      id: initialProgramId,
      name: 'Program 1',
      program_duration: 0,
      duration_unit: 'Days',
      days_per_week: 0,
      main_goal: 'Strength',
      selected: false, // Flag to indicate if the program is selected
      workouts: [
        {
          id: initialWorkoutId,
          name: 'Workout 1',
          programId: initialProgramId,
          selected: true, // Indicator for the active workout
          exercises: []
        }
      ]
    }
  },
  selectedProgramId: null, // Store the ID of the selected program
  activeWorkoutId: initialWorkoutId // Store the ID of the active workout
};
