import React from 'react';
import { useEffect, useRef } from 'react';
import gsap from 'gsap';

// ... other imports

const WorkoutSlider = ({ workouts, activeWorkoutId, onWorkoutChange }) => {
  // References to our DOM elements
  const sliderRef = useRef(null);
  const workoutRefs = useRef(workouts.map(() => React.createRef()));

  useEffect(() => {
    // GSAP initial setup
    gsap.set(
      workoutRefs.current.map(ref => ref.current),
      { xPercent: -100 }
    );
    gsap.set(workoutRefs.current[activeWorkoutId].current, { xPercent: 0 });
  }, [activeWorkoutId, workouts.length]);

  const slideToWorkout = index => {
    const currentWorkout = workoutRefs.current[activeWorkoutId].current;
    const nextWorkout = workoutRefs.current[index].current;

    // Slide out the current workout
    gsap.to(currentWorkout, { duration: 1, xPercent: -100 });

    // Slide in the next workout
    gsap.fromTo(nextWorkout, { xPercent: 100 }, { duration: 1, xPercent: 0 });

    // Callback to update the active workout
    onWorkoutChange(index);
  };

  return (
    <>
      <div className='workout-slider' ref={sliderRef}>
        {workouts.map((workout, index) => (
          <div
            className='workout-container'
            ref={workoutRefs.current[index]}
            key={workout.id}
          >
            {/* Your existing WorkoutContainer JSX here */}
          </div>
        ))}
      </div>
      <div className='controls'>
        <button onClick={() => slideToWorkout(activeWorkoutId - 1)}>
          Prev
        </button>
        <button onClick={() => slideToWorkout(activeWorkoutId + 1)}>
          Next
        </button>
      </div>
    </>
  );
};

export default WorkoutSlider;
