// Only include Monday in the initial days array
export const INITIAL_DAYS = [{ id: 1, name: 'Monday' }];

// Include all possible days for user to add
export const ALL_DAYS = [
  { id: 1, name: 'Monday' },
  { id: 2, name: 'Tuesday' },
  { id: 3, name: 'Wednesday' },
  { id: 4, name: 'Thursday' },
  { id: 5, name: 'Friday' },
  { id: 6, name: 'Saturday' },
  { id: 7, name: 'Sunday' }
];

export const DAY_TYPES = [
  { id: 1, name: 'Day of Week' },
  { id: 2, name: 'Numerical' }
];
