import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';
import exerciseUtils from '../utils/exercise.js';

function workoutReducer(state = initialState.programs, action) {
  switch (action.type) {
    case actionTypes.SET_ACTIVE_WORKOUT: {
      const { workoutId, programId } = action.payload;

      //Check if the workout is already selected
      const program = state[programId];

      // Update the selected state for all workouts in the program
      const updatedWorkouts = state[programId].workouts.map(workout =>
        workout.id === workoutId
          ? { ...workout, selected: true }
          : { ...workout, selected: false }
      );

      return {
        ...state,
        [programId]: {
          ...state[programId],
          workouts: updatedWorkouts
        }
      };
    }

    case actionTypes.ADD_WORKOUT:
      return {
        ...state,
        [action.payload.id]: action.payload
      };

    case actionTypes.UPDATE_WORKOUT:
      return {
        ...state,
        [action.payload.id]: action.payload
      };

    case actionTypes.ADD_EXERCISE:
      const { workoutId: workoutIdAddEx, exercises } = action.payload;
      const existingWorkout = state[workoutIdAddEx];

      if (!existingWorkout) {
        console.error(`Workout with id ${workoutIdAddEx} not found`);
        return state;
      }

      const existingExerciseIds = new Set(
        existingWorkout.exercises.map(ex => ex.catalog_exercise_id)
      );

      const updatedExrc = [
        ...existingWorkout.exercises,
        ...exercises.filter(
          ex => !existingExerciseIds.has(ex.catalog_exercise_id)
        )
      ].map((exercise, index) => ({ ...exercise, order: index + 1 }));

      return {
        ...state,
        [workoutIdAddEx]: {
          ...existingWorkout,
          exercises: updatedExrc
        }
      };

    case actionTypes.REMOVE_EXERCISE:
      const { workoutId } = action.payload;
      const workout = state[workoutId];
      if (!workout) return state;

      const remainingExercises = workout.exercises
        .filter(ex => exerciseUtils.getExerciseId(ex) !== exerciseId)
        .map((exercise, index) => ({ ...exercise, order: index + 1 }));

      return {
        ...state,
        [workoutId]: {
          ...workout,
          exercises: remainingExercises
        }
      };

    case actionTypes.ADD_SET:
      const {
        workoutId: workoutIdAddSet,
        exerciseId,
        weight,
        reps
      } = action.payload;
      const workoutToUpdate = state[workoutIdAddSet];
      if (!workoutToUpdate) return state;

      return {
        ...state,
        [workoutIdAddSet]: {
          ...workoutToUpdate,
          exercises: workoutToUpdate.exercises.map(exercise =>
            exerciseUtils.getExerciseId(exercise) === exerciseId
              ? {
                  ...exercise,
                  sets: [
                    ...exercise.sets,
                    {
                      id: uuidv4(),
                      weight,
                      reps,
                      order: exercise.sets.length + 1
                    }
                  ].map((set, index) => ({ ...set, order: index + 1 }))
                }
              : exercise
          )
        }
      };

    case actionTypes.UPDATE_SET:
      const { workoutId: wId2, exerciseId: exId, updatedSet } = action.payload;
      const workout2 = state[wId2];
      if (!workout2) return state;

      const updatedExercises2 = workout2.exercises.map(exercise => {
        if (exercise.id === exId) {
          return {
            ...exercise,
            sets: exercise.sets.map(set =>
              set.id === updatedSet.id ? { ...set, ...updatedSet } : set
            )
          };
        }
        return exercise;
      });

      return {
        ...state,
        [wId2]: {
          ...workout2,
          exercises: updatedExercises2
        }
      };

    case actionTypes.REMOVE_SET:
      const {
        workoutId: workoutIdDeleteSet,
        exerciseId: exerciseIdDeleteSet,
        setId: setIdDeleteSet
      } = action.payload;
      const workoutDeleteSet = state[workoutIdDeleteSet];
      if (!workoutDeleteSet) return state;

      const updatedExercisesDeleteSet = workoutDeleteSet.exercises.map(
        exercise => {
          const exerciseIdToCompare = exerciseUtils.getExerciseId(exercise);
          if (exerciseIdToCompare === exerciseIdDeleteSet) {
            return {
              ...exercise,
              sets: exercise.sets
                .filter(set => set.id !== setIdDeleteSet)
                .map((set, index) => ({ ...set, order: index + 1 }))
            };
          }
          return exercise;
        }
      );

      return {
        ...state,
        [workoutIdDeleteSet]: {
          ...workoutDeleteSet,
          exercises: updatedExercisesDeleteSet
        }
      };

    case actionTypes.DELETE_WORKOUT: {
      if (!action.payload) {
        console.error('Invalid payload for DELETE_WORKOUT', action.payload);
        return state;
      }

      const { [action.payload]: deletedWorkout, ...remainingWorkouts } = state;
      const reorderedWorkouts = Object.values(remainingWorkouts)
        .sort((a, b) => a.order - b.order)
        .map((workout, index) => ({
          ...workout,
          order: index + 1
        }))
        .reduce((acc, workout) => {
          acc[workout.id] = workout;
          return acc;
        }, {});

      const newState = {
        ...reorderedWorkouts
      };

      return newState;
    }

    default:
      return state;
  }
}

export { workoutReducer };
