import React, { useState, useEffect, useMemo } from 'react';
import useFetchData from '../../hooks/useFetchData';
import TextInput from '../Inputs/TextInput';
import NavBar from '../../../components/Nav/Nav';
import { useTheme } from '../../../contexts/themeContext';

import './programs.css';

const ProgramPage = () => {
  const [inputValue, setInputValue] = useState('');
  const [selectedMainGoal, setSelectedMainGoal] = useState('');
  const [selectedDuration, setSelectedDuration] = useState('');
  const [selectedDaysPerWeek, setSelectedDaysPerWeek] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const { theme } = useTheme();

  const handleInputChange = event => {
    const newValue = event.target.value;
    setInputValue(newValue);
    onSearchTextChange(newValue);
  };

  const {
    data: programs,
    isLoading,
    error
  } = useFetchData('http://localhost:9025/api/programs/2');

  const filteredPrograms = useMemo(() => {
    return programs.filter(exercise => {
      const matchesMainGoal =
        !selectedMainGoal ||
        selectedMainGoal === 'All' ||
        programs.main_goal === selectedMainGoal;
      const matchesDuration =
        !selectedDuration ||
        selectedDuration === 'All' ||
        exercise.program_duration === selectedDuration;
      const matchesDaysPerWeek =
        !selectedDaysPerWeek ||
        selectedDaysPerWeek === 'All' ||
        exercise.equipment === selectedDaysPerWeek;
      const matchesSearchTerm =
        !searchTerm ||
        exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
      return matchesMainGoal && matchesDuration && matchesSearchTerm;
    });
  }, [searchTerm, selectedMuscle, selectedEquipment, exercises]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  console.log('programs from programs page:', programs);
  if (isLoading) return <div>loading...</div>;
  if (error) return <div>Error loading exercises: {error}</div>;

  return (
    <div>
      <NavBar />
      <div className='view-prog-page'>
        <h1 className='view-prog-page__page-title'>My Programs</h1>
        <div className={`exercise-search ${theme}`}>
          <div className='exercise-search__search-input-container'>
            <TextInput
              list='exercises'
              className={`exercise-search__search-text-input ${theme}`}
              id='exercise-search-bar'
              onChange={handleInputChange}
              value={inputValue}
              type='search'
              placeholder='Program Names'
            />
            <datalist id='exercises'>
              {programs.map((program, index) => (
                <option key={index} value={program.name} />
              ))}
            </datalist>
          </div>

          <div className='exercise-search__search-input-container'>
            <input
              list='muscles'
              className={`exercise-search__muscle ${theme}`}
              type='search'
              onChange={event => onMuscleChange(event.target.value)}
              placeholder='Muscles'
            />
            <datalist id='muscles'>
              {muscles.map((option, index) => (
                <option key={index} value={option.name} />
              ))}
            </datalist>
          </div>

          <div className='exercise-search__search-input-container'>
            <input
              list='equipments'
              className={`exercise-search__equipment ${theme}`}
              type='search'
              onChange={event => onEquipmentChange(event.target.value)}
              placeholder='Equipment'
            />
            <datalist id='equipments'>
              {equipments.map((option, index) => (
                <option key={index} value={option.name} />
              ))}
            </datalist>
          </div>
        </div>
      </div>
      <div className='view-prog-page__program-list'>
        {programs.map(program => (
          <div key={program.id} className={`view-prog-page__program ${theme}`}>
            <h2 className='view-prog-page__program-title'>{program.name}</h2>
            <div className='view-prog-page__program-details'>
              <div className='view-prog-page__program-details-section'>
                <p className='view-prog-page__program-details-label'>
                  Main Goal
                </p>
                <p className='view-prog-page__program-details-value'>
                  {program.main_goal}
                </p>
              </div>
              <div className='view-prog-page__program-details-section'>
                <p className='view-prog-page__program-details-label'>
                  Duration
                </p>
                <p className='view-prog-page__program-details-value'>
                  {program.program_duration} {program.duration_unit}
                </p>
              </div>
              <div className='view-prog-page__program-details-section'>
                <p className='view-prog-page__program-details-label'>
                  Days Per Week
                </p>
                <p className='view-prog-page__program-details-value'>
                  {program.days_per_week}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProgramPage;
