import React, { useState } from 'react';

const ProgramForm = ({ program, onSubmit, isEditing }) => {
  const [formValues, setFormValues] = useState({
    name: program?.name || '',
    mainGoal: program.mainGoal || '',
    programDuration: program?.programDuration || '',
    durationUnit: program?.durationUnit || '',
    daysPerWeek: program?.daysPerWeek || '',
    workouts: program?.workouts || []
  });

  const handleChange = e => {
    setFormValues({
      ...formValues,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit(formValues);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor='mainGoal'>Main Goal</label>
        <input
          type='text'
          id='mainGoal'
          name='mainGoal'
          value={formValues.mainGoal}
          onChange={handleChange}
          disabled={!isEditing}
        />
      </div>
      <div>
        <label htmlFor='programDuration'>Program Duration</label>
        <input
          type='number'
          id='programDuration'
          name='programDuration'
          value={formValues.programDuration}
          onChange={handleChange}
          disabled={!isEditing}
        />
      </div>
      <div>
        <label htmlFor='daysPerWeek'>Days Per Week</label>
        <input
          type='number'
          id='daysPerWeek'
          name='daysPerWeek'
          value={formValues.daysPerWeek}
          onChange={handleChange}
          disabled={!isEditing}
        />
      </div>
      {isEditing && <button type='submit'>Save</button>}
    </form>
  );
};

export default ProgramForm;
