import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function exerciseReducer(state = initialState, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      console.log('ADD_EXERCISE action:', action);
      console.log('State:', state);
      console.log('initialState:', initialState);
      const { workoutId, exercise } = action.payload;
      console.log('Workout ID:', workoutId);
      console.log('Exercise:', exercise);
      const activeWorkoutId = state.activeWorkout;
      console.log('Active workout ID:', activeWorkoutId);

      // Ensure that there is an active workout and it exists in the state
      if (!activeWorkoutId || !state.workouts[activeWorkoutId]) {
        console.error(
          'Active workout is undefined or does not exist in the state:',
          activeWorkoutId
        );
        return state; // Exit without making changes if there's no valid active workout
      }

      // Retrieve the current list of exercises for the active workout, defaulting to an empty array if undefined
      const currentExercises = state.workouts[activeWorkoutId].exercises || [];

      console.log('Current exercises:', currentExercises);

      // Return the updated state with the new exercise added to the active workout
      return {
        ...state,
        [workoutId]: [...currentExercises, exercise]
      };

    case actionTypes.UPDATE_EXERCISE:
      console.log('UPDATE_EXERCISE action:', action);
      const { id, updates } = action.payload;
      const workoutContainingExercise = Object.values(state.workouts).find(
        workout => workout.exercises.includes(id)
      );

      if (workoutContainingExercise) {
        return {
          ...state,
          workouts: {
            ...state.workouts,
            [workoutContainingExercise.id]: {
              ...workoutContainingExercise,
              exercises: workoutContainingExercise.exercises.map(ex =>
                ex.id === id ? { ...ex, ...updates } : ex
              )
            }
          }
        };
      } else {
        console.error('Exercise id does not exist in state:', id);
      }
      return state;

    case actionTypes.DELETE_EXERCISE:
      console.log('DELETE_EXERCISE action:', action);
      const newState = { ...state };
      delete newState[action.payload];
      return newState;

    default:
      return state;
  }
}

export { exerciseReducer };
