import { v4 as uuidv4 } from 'uuid';

// Static IDs for initial state
const initialProgramId = uuidv4();
const initialWorkoutId = uuidv4();

export const initialState = {
  program: {},
  workouts: {
    [initialProgramId]: {
      id: initialWorkoutId,
      name: 'Workout 1',
      exercises: []
    }
  },
  activeWorkout: initialWorkoutId,
  selectedProgram: null
};
