import { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useInView } from 'react-intersection-observer';
import NavBar from '../../components/Nav/Nav';
import './home.css';

const HomePage = () => {
  const [activeDiv, setActiveDiv] = useState(null);
  const navigate = useNavigate();

  const childRefs = useRef([]);

  const navigateToProgramPage = () => {
    navigate('/programs');
  };

  const navigateToCreateProgramPage = () => {
    navigate('/create-program');
  };

  const scrollToNextPage = () => {
    window.scrollTo({
      top: window.innerHeight,
      behavior: 'smooth'
    });
  };

  const handleClick = divId => {
    setActiveDiv(activeDiv === divId ? null : divId);
  };

  const handleMouseEnter = divId => {
    setActiveDiv(divId);
  };

  const handleMouseLeave = () => {
    setActiveDiv(null); // Optional: Remove active state on mouse leave
  };

  return (
    <div>
      <NavBar />
      <div className='full-viewport-div'>
        <h1 className='main-title'>Pr00f 0f W0rk</h1>
        <div className='arrow' onClick={scrollToNextPage}>
          â†“
        </div>
      </div>
      <div className='container'>
        <div
          className={`child left ${activeDiv === 'div1' ? 'active' : ''} ${
            childRefs.current[0]?.inView ? 'visible' : ''
          }`}
          onClick={navigateToCreateProgramPage}
          onMouseEnter={() => handleMouseEnter('div1')}
          onMouseLeave={handleMouseLeave}
          ref={el => (childRefs.current[0] = useInView(el, { threshold: 0.4 }))}
        >
          <h2>Programs</h2>
          <h1>Start Program</h1>
          <img src='path/to/image.jpg' alt='Image 1' />
        </div>
        <div
          className={`child center ${activeDiv === 'div2' ? 'active' : ''} ${
            childRefs.current[1]?.inView ? 'visible' : ''
          }`}
          onClick={navigateToProgramPage}
          onMouseEnter={() => handleMouseEnter('div2')}
          onMouseLeave={handleMouseLeave}
          ref={el => (childRefs.current[1] = useInView(el, { threshold: 0.4 }))}
        >
          <h2>Workout</h2>
          <h1>Workout Using Program</h1>
          <img src='path/to/image.jpg' alt='Image 2' />
        </div>
        <div
          className={`child right ${activeDiv === 'div3' ? 'active' : ''} ${
            childRefs.current[2]?.inView ? 'visible' : ''
          }`}
          onClick={() => handleClick('div3')}
          onMouseEnter={() => handleMouseEnter('div3')}
          onMouseLeave={handleMouseLeave}
          ref={el => (childRefs.current[2] = useInView(el, { threshold: 0.4 }))}
        >
          <h2>Workout</h2>
          <h1>Quick Workout</h1>
          <img src='path/to/image.jpg' alt='Image 3' />
        </div>
      </div>
    </div>
  );
};

export default HomePage;
