import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { ProgramContext } from '../../../contexts/programContext';
import Button from '../../../components/Inputs/Button';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';

import './program.css';

const EditProgram = () => {
  const { state, updateProgram, addWorkout, selectWorkout, clearState } =
    useContext(ProgramContext);
  const [expandedWorkouts, setActiveWorkout] = useState({});
  const navigate = useNavigate();

  const program = state.programs.selectedProgram;
  // console.log('selectedProgram', program);

  if (!program) {
    return <div>Loading...</div>;
  }

  const handleExpandWorkout = workoutId => {
    setActiveWorkout(workoutId);
  };

  // const handleToggleProgramForm = () => {
  //   setExpandedWorkouts(prevState => ({
  //     ...Object.keys(prevState).reduce((acc, key) => {
  //       acc[key] = false; // collapse all workouts
  //       return acc;
  //     }, {}),
  //     program: !prevState.program // toggle the program form
  //   }));
  // };

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    // console.log('handleAddWorkout event', event);
    // console.log('handleAddWorkout program', program);
    event.preventDefault();
    if (program && program.id) {
      addWorkout(program.id);
    }
  };
  const handleUpdateProgram = async () => {
    try {
      const updatedProgram = {
        ...program,
        workouts: program.workouts.map(workout => {
          const updatedWorkout = state.workouts[workout.id];
          return updatedWorkout
            ? {
                ...updatedWorkout,
                exercises: updatedWorkout.exercises.map(exercise => ({
                  ...exercise,
                  sets: exercise.sets.map(set => ({
                    ...set,
                    weight: parseInt(set.weight, 10) || 0,
                    reps: parseInt(set.reps, 10) || 0
                  }))
                }))
              }
            : workout;
        })
      };
      // console.log('updatedProgram', updatedProgram);

      const savedProgram = await updateProgram(updatedProgram);

      navigate(`/programs/${savedProgram.id}`);
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  return (
    <div>
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={program}
              isEditing={true}
              isNewProgram={false}
              isExpanded={expandedWorkouts['program']}
              // onToggleExpand={handleToggleProgramForm}
            />
            {program && program.workouts && program.workouts.length > 0 ? (
              program.workouts.map(workout => {
                if (!workout || !workout.id) {
                  console.error('Invalid workout object:', workout); // Log invalid workout object
                  return null;
                }
                const fullWorkout = state.workouts[workout.id] || workout;
                return (
                  <Workout
                    key={workout.id}
                    isEditing={true}
                    isNewProgram={false}
                    workout={fullWorkout}
                    programId={program.id}
                    isExpanded={expandedWorkouts[workout.id] || false}
                    onToggleExpand={() => handleExpandWorkout(workout.id)}
                  />
                );
              })
            ) : (
              <div>No workouts available</div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleUpdateProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default EditProgram;
