import { initialState } from './initialState';

function programReducer(state = initialState.programs, action) {
  switch (action.type) {
    case 'ADD_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      const newState = {
        ...state,
        [id]: {
          id, // Ensure the ID is included
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };
      console.log('Reducer added new program state:', newState);
      return newState;
    }

    case 'UPDATE_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      } = action.payload;

      const existingProgram = state[id];
      if (!existingProgram) {
        console.error('Program not found:', id);
        return state;
      }

      const updatedState = {
        ...state,
        [id]: {
          ...existingProgram, // Spread the existing program details
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal,
          user_id
        }
      };

      return updatedState;
    }

    case 'UPDATE_PROGRAM_SUCCESS': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      } = action.payload;

      console.log('Handling UPDATE_PROGRAM_SUCCESS for ID:', id);

      const existingProgram = state[id];
      if (!existingProgram) {
        console.error('Program not found:', id);
        return state;
      }

      const updatedProgram = {
        ...existingProgram,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      };

      return {
        ...state,
        [id]: updatedProgram
      };
    }

    case 'SET_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      } = action.payload;

      return {
        ...state,
        [id]: {
          id,
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal,
          user_id
        }
      };
    }

    case 'DELETE_PROGRAM': {
      const { programId } = action.payload;

      if (!programId) {
        console.error('Invalid payload for DELETE_PROGRAM', action.payload);
        return state;
      }

      // console.log(`Deleting program with ID: ${programId}`);
      const newPrograms = { ...state.programs };
      delete newPrograms[programId];

      const newState = {
        ...state,
        programs: newPrograms
      };

      // console.log('State After DELETE_PROGRAMS:', newState);
      return newState;
    }

    default:
      return state;
  }
}

export { programReducer };
