import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function workoutReducer(state = initialState, action) {
  console.log('Action Type:', action.type);
  console.log('State Before:', state);

  switch (action.type) {
    case actionTypes.ADD_WORKOUT: {
      if (!action.payload || !action.payload.programId) {
        console.error('Invalid payload for ADD_WORKOUT', action.payload);
        return state;
      }

      const workoutId = uuidv4();
      const workoutTitle =
        action.payload.name ||
        `Workout ${Object.keys(state.workouts).length + 1}`;
      const newWorkout = {
        id: workoutId,
        name: workoutTitle,
        exercises: [],
        programId: action.payload.programId
      };

      const newState = {
        ...state,
        workouts: {
          ...state.workouts,
          [workoutId]: newWorkout
        }
      };
      console.log('State After ADD_WORKOUT:', newState);
      return newState;
    }

    case actionTypes.UPDATE_WORKOUT: {
      if (!action.payload || !action.payload.id) {
        console.error('Invalid payload for UPDATE_WORKOUT', action.payload);
        return state;
      }

      const workout = state.workouts[action.payload.id];
      if (!workout) {
        console.error(
          'Workout not found for UPDATE_WORKOUT',
          action.payload.id
        );
        return state;
      }

      const newState = {
        ...state,
        workouts: {
          ...state.workouts,
          [action.payload.id]: {
            ...workout,
            ...action.payload
          }
        }
      };
      console.log('State After UPDATE_WORKOUT:', newState);
      return newState;
    }

    case actionTypes.DELETE_WORKOUT: {
      if (!action.payload) {
        console.error('Invalid payload for DELETE_WORKOUT', action.payload);
        return state;
      }

      const newWorkouts = { ...state.workouts };
      delete newWorkouts[action.payload];
      const newState = {
        ...state,
        workouts: newWorkouts
      };
      console.log('State After DELETE_WORKOUT:', newState);
      return newState;
    }

    case actionTypes.SET_ACTIVE_WORKOUT: {
      if (!action.payload || !action.payload.workoutId) {
        console.warn('Invalid payload for SET_ACTIVE_WORKOUT', action.payload);
        return state;
      }

      const newState = {
        ...state,
        activeWorkout: action.payload.workoutId
      };
      console.log('State After SET_ACTIVE_WORKOUT:', newState);
      return newState;
    }

    default:
      console.log('State After Default:', state);
      return state;
  }
}

export { workoutReducer };
