import React, { useContext, useState, useEffect, useMemo } from 'react';
import { TbPencil } from 'react-icons/tb';
import { BsChevronCompactUp, BsChevronCompactDown } from 'react-icons/bs';
import { IoCloseCircleSharp, IoCheckmarkCircleSharp } from 'react-icons/io5';
import { MdDragHandle, MdAddBox } from 'react-icons/md';
import { useDrag, useDrop, DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { RiDeleteBack2Fill } from 'react-icons/ri';
import { TbHttpDelete } from 'react-icons/tb';
import TextInput from '../Inputs/TextInput';
import { ProgramContext } from '../../contexts/programContext';
import { useTheme } from '../../contexts/themeContext';
import { useNavigate } from 'react-router-dom';
import './Workout.css';

const ItemTypes = {
  EXERCISE: 'exercise'
};

// Draggable Exercise Component
const DraggableExercise = ({
  exercise,
  index,
  moveExercise,
  handleAddSet,
  handleUpdateSetLocally,
  handleUpdateSetOnBlur,
  handleRemoveSet,
  handleDeleteExercise
}) => {
  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.EXERCISE,
    item: { index },
    collect: monitor => ({
      isDragging: !!monitor.isDragging()
    })
  });

  const [, drop] = useDrop({
    accept: ItemTypes.EXERCISE,
    hover: draggedItem => {
      if (draggedItem.index !== index) {
        moveExercise(draggedItem.index, index);
        draggedItem.index = index;
      }
    }
  });

  return (
    <div
      ref={node => drag(drop(node))}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      className='workout__each-exercise'
    >
      <div className='workout__exercise-column'>
        <div className='workout__exercise-info'>
          <div className='workout__drag-order-container'>
            <span className='workout__exercise-order-number'>
              {exercise.order}
            </span>
          </div>
          <div className='workout__exercise-details'>
            <h4 className='workout__exercises_name'>{exercise.name}</h4>
            <h5 className='workout__exercise-muscle'>{exercise.muscle}</h5>
          </div>
        </div>
      </div>

      <div className='workout__sets-column'>
        {exercise.sets?.map(set => (
          <div key={set.id} className='workout__set'>
            <p className='workout__set-order-number'>{set.order}</p>
            <TextInput
              className='workout__set-weight'
              onChange={e =>
                handleUpdateSetLocally(
                  { weight: e.target.value },
                  exercise.catalog_exercise_id,
                  set.id
                )
              }
              onBlur={() =>
                handleUpdateSetOnBlur(exercise.catalog_exercise_id, set)
              }
              value={set.weight}
              type='number'
            />
            <TextInput
              className='workout__set-reps'
              onChange={e =>
                handleUpdateSetLocally(
                  { reps: e.target.value },
                  exercise.catalog_exercise_id,
                  set.id
                )
              }
              onBlur={() =>
                handleUpdateSetOnBlur(exercise.catalog_exercise_id, set)
              }
              value={set.reps}
              type='number'
            />
            {set.order > 1 && (
              <button
                onClick={() => handleRemoveSet(exercise.id, set.id)}
                className='workout__delete-set-btn'
              >
                <RiDeleteBack2Fill size={25} />
              </button>
            )}
          </div>
        ))}
        <button
          onClick={() => handleAddSet(exercise)}
          className='workout__add-set-btn'
        >
          <MdAddBox size={25} />
        </button>
      </div>

      <div className='workout__exercise-controls'>
        <div className='workout__drag-handle'>
          <MdDragHandle size={25} />
        </div>
        <button
          className='workout__remove-exercise-btn'
          onClick={() => handleDeleteExercise(exercise.id)}
        >
          <TbHttpDelete size={30} />
        </button>
      </div>
    </div>
  );
};

const Workout = ({
  workout: initialWorkout,
  isEditing,
  isNewProgram,
  programId,
  isExpanded,
  onToggleExpand
}) => {
  const {
    state,
    setActiveWorkout,
    updateWorkoutField,
    deleteWorkout,
    removeExercise,
    updateWorkout,
    addSet,
    updateSet,
    removeSet
  } = useContext(ProgramContext);

  const workouts = state.workout.workouts;
  const activeWorkout = state.workout.activeWorkout;

  const workout = useMemo(() => {
    return workouts.find(w => w.id === initialWorkout.id) || initialWorkout;
  }, [workouts, initialWorkout]);

  const [isEditingTitle, setIsEditingTitle] = useState(false);
  const [workoutTitle, setWorkoutTitle] = useState(workout.name);
  const [localExercises, setLocalExercises] = useState(workout.exercises);
  const { theme } = useTheme();
  const navigate = useNavigate();

  useEffect(() => {
    if (workout) {
      setWorkoutTitle(workout.name);
      setLocalExercises(workout.exercises);
    }
  }, [workout]);

  const handleSaveTitle = () => {
    if (workout) {
      const updatedWorkout = { ...workout, name: workoutTitle };
      updateWorkout(updatedWorkout, isNewProgram);
    }
    setIsEditingTitle(false);
  };

  const moveExercise = (fromIndex, toIndex) => {
    const updatedExercises = [...localExercises];
    const [movedExercise] = updatedExercises.splice(fromIndex, 1);
    updatedExercises.splice(toIndex, 0, movedExercise);
    setLocalExercises(
      updatedExercises.map((ex, idx) => ({ ...ex, order: idx + 1 }))
    );
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        className={`workout ${theme} ${
          activeWorkout === workout.id ? 'active' : ''
        }`}
      >
        <div className='workout__header'>
          <button
            className='workout__expand-btn'
            onClick={() => onToggleExpand(workout.id)}
          >
            {isExpanded ? (
              <BsChevronCompactUp size={30} />
            ) : (
              <BsChevronCompactDown size={30} />
            )}
          </button>
          <div className='workout__title-container'>
            {isEditingTitle ? (
              <div>
                <input
                  className='workout__title-input'
                  type='text'
                  value={workoutTitle}
                  onBlur={handleSaveTitle}
                  onChange={e => setWorkoutTitle(e.target.value)}
                  placeholder='Enter Workout Title'
                />
                <IoCheckmarkCircleSharp size={25} onClick={handleSaveTitle} />
                <IoCloseCircleSharp
                  size={25}
                  onClick={() => setIsEditingTitle(false)}
                />
              </div>
            ) : (
              <h2 className='workout__title'>{workoutTitle}</h2>
            )}
          </div>
        </div>

        <div className='workout__exercises'>
          {localExercises.map((exercise, index) => (
            <DraggableExercise
              key={exercise.id}
              exercise={exercise}
              index={index}
              moveExercise={moveExercise}
              handleAddSet={addSet}
              handleUpdateSetLocally={handleUpdateSetLocally}
              handleUpdateSetOnBlur={handleUpdateSetOnBlur}
              handleRemoveSet={removeSet}
              handleDeleteExercise={removeExercise}
            />
          ))}
        </div>
      </div>
    </DndProvider>
  );
};

export default Workout;
