// src/pages/ExercisesListPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const ExercisesListPage = () => {
  const { muscle, equipment } = useParams();
  const [selectedMuscle, setSelectedMuscle] = useState(muscle || 'all');
  const [selectedEquipment, setSelectedEquipment] = useState(
    equipment || 'Any Category'
  );
  const [exercises, setExercises] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Function to fetch exercises based on selected muscle and equipment
    const fetchExercises = async () => {
      // Your API call here, e.g., `/api/exercises?muscle=${selectedMuscle}&equipment=${selectedEquipment}`
      // Update setExercises with the fetched data
    };

    fetchExercises();
  }, [selectedMuscle, selectedEquipment]);

  const handleMuscleChange = newMuscle => {
    setSelectedMuscle(newMuscle);
    navigate(`/exercises/${newMuscle}/${selectedEquipment}`);
  };

  const handleEquipmentChange = newEquipment => {
    setSelectedEquipment(newEquipment);
    navigate(`/exercises/${selectedMuscle}/${newEquipment}`);
  };

  return (
    <div>
      <select
        value={selectedMuscle}
        onChange={e => handleMuscleChange(e.target.value)}
      >
        <option value='Any Body Part'>Any Body Part</option>
        {/* Map through your muscles data to create options */}
      </select>

      <select
        value={selectedEquipment}
        onChange={e => handleEquipmentChange(e.target.value)}
      >
        <option value='Any Category'>Any Category</option>
        {/* Map through your equipment data to create options */}
      </select>
    </div>
  );
};

export default ExercisesListPage;
