import { initialState } from './initialState';

function programReducer(state = initialState.programs, action) {
  switch (action.type) {
    case 'ADD_PROGRAM':
      console.log('Adding program:', action.payload);
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      if (!state[id]) {
        console.error('Program not found:', id);
        return state; // Return the current state if the program ID does not exist.
      }

      const updatedState = {
        ...state,
        [id]: {
          ...state[id], // Spread the existing program details
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };

      return updatedState;

    case 'UPDATE_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      } = action.payload;

      // Check if selectedProgram exists and has the correct structure
      if (!state.selectedProgram || !state.selectedProgram.selectedProgram) {
        console.error('Selected program not found in state');
        return state;
      }

      const existingProgram = state.selectedProgram.selectedProgram;
      if (existingProgram.id !== id) {
        console.error('Program ID mismatch:', id);
        return state;
      }

      const updatedProgram = {
        ...existingProgram,
        name: name || existingProgram.name,
        program_duration: program_duration || existingProgram.program_duration,
        duration_unit: duration_unit || existingProgram.duration_unit,
        days_per_week: days_per_week || existingProgram.days_per_week,
        main_goal: main_goal || existingProgram.main_goal,
        user_id: user_id || existingProgram.user_id
      };

      return {
        ...state,
        selectedProgram: {
          ...state.selectedProgram,
          selectedProgram: updatedProgram
        }
      };
    }

    case 'UPDATE_PROGRAM_SUCCESS': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal,
        user_id
      } = action.payload;

      const existingProgram = state[id];
      if (!existingProgram) {
        console.error('Program not found:', id);
        return state;
      }

      const updatedState = {
        ...state,
        [id]: {
          ...existingProgram,
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal,
          user_id
        }
      };

      return updatedState;
    }

    case 'DELETE_PROGRAM': {
      const { programId } = action.payload;

      if (!programId) {
        console.error('Invalid payload for DELETE_PROGRAM', action.payload);
        return state;
      }

      const newPrograms = { ...state.programs };
      delete newPrograms[programId];

      const newState = {
        ...state,
        programs: newPrograms
      };

      return newState;
    }

    default:
      return state;
  }
}

export { programReducer };
