import { actionTypes } from '../actions/actionTypes';
import { v4 as uuidv4 } from 'uuid';

function exerciseReducer(state = {}, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE: {
      const { workoutId, exercises } = action.payload;
      const currentExercises = state[workoutId] || [];
      const exerciseIds = currentExercises.map(ex => ex.id);

      const newExercises = exercises
        .filter(ex => !exerciseIds.includes(ex.id))
        .map((exercise, index) => ({
          ...exercise,
          order: currentExercises.length + index + 1,
          id: uuidv4(), // Ensure a unique id for each exercise instance
          catalog_exercise_id: exercise.id,
          sets: [
            {
              id: uuidv4(),
              order: 1,
              weight: 10,
              reps: 10
            }
          ]
        }));

      return {
        ...state,
        [workoutId]: [...currentExercises, ...newExercises]
      };
    }

    case actionTypes.DELETE_EXERCISE: {
      if (!action.payload) {
        console.error('Invalid payload for DELETE_EXERCISE', action.payload);
        return state;
      }
      const { [action.payload]: deletedExercise, ...remainingExercises } =
        state;
      const reorderedExercises = Object.values(remainingExercises)
        .sort((a, b) => a.order - b.order)
        .map((exercise, index) => ({
          ...exercise,
          order: index + 1
        }))
        .reduce((acc, exercise) => {
          acc[exercise.id] = exercise;
          return acc;
        }, {});

      const newState = {
        ...reorderedExercises
      };

      return newState;
    }

    default:
      return state;
  }
}

export { exerciseReducer };
