import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function setReducer(state = initialState.sets, action) {
  switch (action.type) {
    case actionTypes.ADD_SET: {
      const { exerciseId, weight, reps } = action.payload;
      const setId = uuidv4();
      const newSet = {
        id: setId,
        weight,
        reps,
        order: (state[exerciseId] || []).length + 2
      };

      console.log('Adding new set:', newSet);
      console.log('State before:', state);

      const newState = {
        ...state,
        [exerciseId]: [...(state[exerciseId] || []), newSet]
      };

      console.log('State after:', newState);

      return newState;
    }

    case actionTypes.UPDATE_SET: {
      const { exerciseId, updatedSet } = action.payload;
      console.log('Updating set:', updatedSet);

      return {
        ...state,
        [exerciseId]: state[exerciseId].map(set =>
          set.id === updatedSet.id ? { ...set, ...updatedSet } : set
        )
      };
    }

    case actionTypes.DELETE_SET: {
      const { exerciseId, setId } = action.payload;
      console.log('Deleting set with id:', setId);

      // Get all the sets for the exercise, including the ined from the exercise object and the state
      const exerciseSets = state[exerciseId] || [];
      console.log('exerciseSets:', exerciseSets);
      const exercise = state.exercises?.find(ex => ex.id === exerciseId);
      console.log('exercise:', exercise);
      const initialState = exercise ? exercise.sets : [];
      console.log('initialState:', initialState);

      // Combine the initial sets with the state sets
      const allSets = [...initialState, ...exerciseSets];
      console.log('allSets:', allSets);

      // Filter out the set to delete
      const updatedSets = allSets.filter(set => set.id !== setId);

      // Recalculate the order of the remaining sets
      const reorderedSets = updatedSets.map((set, index) => ({
        ...set,
        order: index + 1
      }));

      return {
        ...state,
        [exerciseId]: reorderedSets
      };
    }
    default:
      return state;
  }
}

export { setReducer };
