import { createContext, useReducer, useCallback, useMemo } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import rootReducer from '../reducers/rootReducer';
import { initialState } from '../reducers/initialState';
import {
  createNewWorkout,
  createNewExercise,
  createNewSet
} from '../utils/programStructures';

import exerciseUtils from '../utils/exercise.js';

export const ProgramContext = createContext();

export const ProgramProvider = ({ children }) => {
  const [state, dispatchBase] = useReducer(rootReducer, initialState);

  // Program Actions

  const dispatch = useMemo(() => dispatchBase, []);

  const createInitialProgram = () => {
    console.log('Creating initial program');
    const newProgramId = uuidv4();
    const newWorkoutId = uuidv4();
    const initialProgram = {
      id: newProgramId,
      tempId: newProgramId,
      name: 'New Program',
      program_duration: 0,
      duration_unit: 'Weeks',
      days_per_week: 0,
      main_goal: '',
      user_id: null,
      isNew: true,
      workouts: [
        {
          id: newWorkoutId,
          tempId: newWorkoutId,
          name: 'Workout 1',
          programId: newProgramId,
          exercises: [],
          order: 1,
          isNew: true
        }
      ]
    };
    console.log('Initial program created:', initialProgram);
    return initialProgram;
  };

  const prepareProgramForSave = program => {
    const { tempId, isNew, ...programToSave } = program;
    return {
      ...programToSave,
      workouts: Object.values(program.workouts).map(prepareWorkoutForSave)
    };
  };

  const setSelectedProgram = program => {
    console.log('Setting selected program:', program);
    dispatch({
      type: 'SET_SELECTED_PROGRAM',
      payload: program
    });
  };

  const saveProgram = async () => {
    const programToSave = prepareProgramForSave(
      state.programs[state.selectedProgramId]
    );

    console.log('Saving program with payload:', programToSave);

    dispatch({ type: 'SAVE_PROGRAM_START' });
    try {
      validateProgramData(programToSave);
      const response = await fetch('http://localhost:9025/api/programs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(programToSave)
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error saving program:', errorText);
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();
      dispatch({
        type: 'SAVE_PROGRAM_SUCCESS',
        payload: savedProgram
      });
    } catch (error) {
      console.error('Failed to save program:', error);
      dispatch({
        type: 'SAVE_PROGRAM_FAILURE',
        payload: error.message
      });
    }
  };

  const updateProgram = async updatedProgram => {
    dispatch({ type: 'SAVE_PROGRAM_START' });
    try {
      const programToUpdate = prepareProgramForSave(updatedProgram);
      validateProgramData(programToUpdate);

      console.log('Sending updated program to server:', programToUpdate);

      const response = await fetch(
        `http://localhost:9025/api/programs/${programToUpdate.id}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(programToUpdate)
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error updating program:', errorText);
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();

      dispatch({
        type: 'UPDATE_PROGRAM_SUCCESS',
        payload: savedProgram
      });
    } catch (error) {
      console.error('Failed to update program:', error);
      dispatch({
        type: 'SAVE_PROGRAM_FAILURE',
        payload: error.message
      });
    }
  };

  const updateWorkoutAndProgram = updatedWorkout => {
    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: updatedWorkout
    });
  };

  const validateProgramData = programData => {
    if (!programData.workouts || !Array.isArray(programData.workouts)) {
      throw new Error('Workouts should be an array.');
    }
    programData.workouts.forEach(workout => {
      if (!workout.exercises || !Array.isArray(workout.exercises)) {
        throw new Error('Exercises should be an array.');
      }
      workout.exercises.forEach(exercise => {
        if (!exercise.sets || !Array.isArray(exercise.sets)) {
          throw new Error('Sets should be an array.');
        }
      });
    });
  };

  const addProgram = details => {
    dispatch({
      type: actionTypes.ADD_PROGRAM,
      payload: details
    });
  };

  const deleteProgram = async programId => {
    try {
      const response = await fetch(
        `http://localhost:9025/api/programs/${programId}`,
        {
          method: 'DELETE'
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error deleting program:', errorText);
        throw new Error('Failed to delete program');
      }

      dispatch({
        type: actionTypes.DELETE_PROGRAM,
        payload: { programId }
      });
    } catch (error) {
      console.error('Failed to delete program:', error);
    }
  };

  // Workout Actions

  const prepareWorkoutForSave = workout => {
    const { tempId, isNew, ...workoutToSave } = workout;
    return {
      ...workoutToSave,
      exercises: workout.exercises.map(prepareExerciseForSave)
    };
  };

  const setActiveWorkout = workoutId => {
    if (!workoutId) {
      console.error('Attempted to set active workout without a valid ID');
      return; // Optionally return to avoid dispatching undefined ID
    }
    dispatch({
      type: actionTypes.SET_ACTIVE_WORKOUT,
      payload: workoutId
    });
  };

  const addWorkout = programId => {
    const newWorkout = createNewWorkout();
    newWorkout.name = 'New Workout';
    newWorkout.order =
      Object.keys(state.programs[programId].workouts).length + 1;

    dispatch({
      type: actionTypes.ADD_WORKOUT,
      payload: { programId, workout: newWorkout }
    });
  };

  const updateWorkout = (programId, workout) => {
    const updatedWorkout = {
      ...workout,
      isNew: false
    };

    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: { programId, workout: updatedWorkout }
    });
  };

  const deleteWorkout = (programId, workoutId) => ({
    type: actionTypes.DELETE_WORKOUT,
    payload: { programId, workoutId }
  });

  // Exercise Actions

  const prepareExerciseForSave = exercise => {
    const { tempId, isNew, ...exerciseToSave } = exercise;
    return {
      ...exerciseToSave,
      sets: exercise.sets.map(prepareSetForSave)
    };
  };

  const addExercise = (workoutId, exercises) => {
    const standardizedExercises = exercises.map(ex => {
      if (ex.id) {
        // This is an existing exercise (from editing)
        return {
          ...createNewExercise(),
          ...ex,
          isNew: false
        };
      } else {
        // This is a new exercise being added
        return createNewExercise(ex);
      }
    });

    dispatch({
      type: actionTypes.ADD_EXERCISE,
      payload: { workoutId, exercises: standardizedExercises }
    });
  };

  const removeExercise = (workoutId, exerciseId) => {
    console.log('Removing exercise:', exerciseId, 'from workout:', workoutId);
    dispatch({
      type: actionTypes.REMOVE_EXERCISE,
      payload: { workoutId, exerciseId }
    });
  };

  const toggleExerciseSelection = (exerciseId, exerciseData) => {
    if (!state.activeWorkoutId) {
      console.error('No active workout selected');
      return;
    }

    dispatch({
      type: actionTypes.TOGGLE_EXERCISE_SELECTION,
      payload: {
        exerciseIdForToggle: exerciseId,
        exerciseData: exerciseData
      }
    });
  };

  // Set Actions

  const prepareSetForSave = set => {
    const { tempId, isNew, ...setToSave } = set;
    return setToSave;
  };

  const addSet = (workoutId, exerciseId, weight = 10, reps = 10) => {
    console.log(
      'Adding set for workoutId:',
      workoutId,
      'exerciseId:',
      exerciseId
    );

    const workout = state.programs[state.selectedProgramId].workouts[workoutId];
    if (!workout) {
      console.error('Workout not found:', workoutId);
      return;
    }

    const exercise = workout.exercises.find(
      ex => exerciseUtils.getExerciseId(ex) === exerciseId
    );
    if (!exercise) {
      console.error(
        'Exercise not found:',
        exerciseId,
        'in workout:',
        workoutId
      );
      return;
    }

    const newSet = createNewSet();
    newSet.weight = weight;
    newSet.reps = reps;
    newSet.order = exercise.sets.length + 1;

    dispatch({
      type: actionTypes.ADD_SET,
      payload: { workoutId, exerciseId, newSet }
    });
  };

  const updateSet = (workoutId, exerciseId, updatedSet) => {
    dispatch({
      type: actionTypes.UPDATE_SET,
      payload: { workoutId, exerciseId, updatedSet }
    });
  };

  const removeSet = (workoutId, exerciseId, setId) => {
    const workout = state.programs[state.selectedProgramId].workouts[workoutId];
    if (!workout) {
      console.error('Workout not found:', workoutId);
      return;
    }

    const exercise = workout.exercises.find(
      ex => exerciseUtils.getExerciseId(ex) === exerciseId
    );
    if (!exercise) {
      console.error(
        'Exercise not found:',
        exerciseId,
        'in workout:',
        workoutId
      );
      return;
    }

    const setExists = exercise.sets.some(
      set => set.id === setId || set.tempId === setId
    );
    if (!setExists) {
      console.error('Set not found:', setId, 'in exercise:', exerciseId);
      return;
    }

    dispatch({
      type: actionTypes.REMOVE_SET,
      payload: { workoutId, exerciseId, setId }
    });
  };

  const clearState = () => {
    dispatch({ type: actionTypes.CLEAR_STATE });
  };

  return (
    <ProgramContext.Provider
      value={{
        state,
        dispatch,
        activeWorkout: state.activeWorkout,
        createInitialProgram,
        setSelectedProgram,
        addProgram,
        updateProgram,
        updateWorkoutAndProgram,
        deleteProgram,
        addWorkout,
        updateWorkout,
        deleteWorkout,
        setActiveWorkout,
        addExercise,
        toggleExerciseSelection,
        removeExercise,
        addSet,
        updateSet,
        removeSet,
        saveProgram,
        clearState
      }}
    >
      {children}
    </ProgramContext.Provider>
  );
};
