import { actionTypes } from '../actions/actionTypes';
// import { initialState } from './initialState';

export function programReducer(state, action) {
  switch (action.type) {
    case actionTypes.SELECT_PROGRAM:
      return state.map(program => ({
        ...program,
        selected: program.id === action.payload
      }));

    case actionTypes.UPDATE_PROGRAM:
      return action.payload;

    case actionTypes.ADD_WORKOUT:
      return {
        ...state,
        workouts: [...state.workouts, action.payload]
      };
    case actionTypes.UPDATE_WORKOUT:
      return {
        ...state,
        workouts: state.workouts.map(w =>
          w.id === action.payload.id ? action.payload : w
        )
      };
    case actionTypes.DELETE_WORKOUT:
      return {
        ...state,
        workouts: state.workouts.filter(w => w.id !== action.payload)
      };
    default:
      return state;
  }
}
