// ProgramContext.js
import React, { createContext, useReducer, useContext } from 'react';
import mainReducer from './rootReducer';
import { initialState } from './initialState';
import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';

const ProgramContext = createContext();

export function ProgramProvider({ children }) {
  const [state, dispatch] = useReducer(mainReducer, initialState);

  const updateProgram = updatedProgram => {
    dispatch({ type: actionTypes.UPDATE_PROGRAM, payload: updatedProgram });
  };

  const setSelectedWorkout = workoutId => {
    dispatch({ type: actionTypes.SET_SELECTED_WORKOUT_ID, payload: workoutId });
  };

  const addWorkout = () => {
    const newWorkout = {
      id: uuidv4(),
      name: `Workout ${state.programs.workouts.length + 1}`,
      selected: false,
      exercises: []
    };
    dispatch({ type: actionTypes.ADD_WORKOUT, payload: newWorkout });
  };

  const updateWorkout = updatedWorkout => {
    dispatch({ type: actionTypes.UPDATE_WORKOUT, payload: updatedWorkout });
  };

  const deleteWorkout = workoutId => {
    dispatch({ type: actionTypes.DELETE_WORKOUT, payload: workoutId });
  };

  const addExercise = (workoutId, exercise) => {
    dispatch({
      type: actionTypes.ADD_EXERCISE,
      payload: { workoutId, exercises: [exercise] }
    });
  };

  const removeExercise = (workoutId, exerciseId) => {
    dispatch({
      type: actionTypes.REMOVE_EXERCISE,
      payload: { workoutId, exerciseId }
    });
  };

  const clearState = () => {
    dispatch({ type: actionTypes.CLEAR_STATE });
  };

  return (
    <ProgramContext.Provider
      value={{
        state,
        updateProgram,
        setSelectedWorkout,
        addWorkout,
        updateWorkout,
        deleteWorkout,
        addExercise,
        removeExercise,
        clearState
      }}
    >
      {children}
    </ProgramContext.Provider>
  );
}

export function useProgram() {
  const context = useContext(ProgramContext);
  if (!context) {
    throw new Error('useProgram must be used within a ProgramProvider');
  }
  return context;
}
