import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logoSvg from '../../assets/concentric_circles.svg';
import { FiMenu, FiX } from 'react-icons/fi';
import './Nav.css';

const Navbar = ({ theme, toggleTheme }) => {
  const [isActive, setIsActive] = useState(false);
  const [menuClicked, setMenuClicked] = useState(false);

  const toggleSwitch = () => {
    setIsActive(current => !current);
  };
  return (
    <nav className={`navbar ${theme}`}>
      <img src={logoSvg} alt='POW' className='navbar-logo' />
      {menuClicked ? (
        <FiX size={25} className={'navbar_menu'} onClick={toggleMenuClick} />
      ) : (
        <FiMenu size={25} className={'navbar_menu'} onClick={toggleMenuClick} />
      )}
      <ul className='nav-links'>
        <li>
          <Link to='/programs' className='nav-item'>
            Programs
          </Link>
        </li>
        <li>
          <Link to='/workouts' className='nav-item'>
            Workouts
          </Link>
        </li>
        <li>
          <Link to='/exercises' className='nav-item'>
            Exercises
          </Link>
        </li>
        <li>
          <Link to='/progress' className='nav-item'>
            Progress
          </Link>
        </li>
        <li>
          <Link to='/profile' className='nav-item'>
            Profile
          </Link>
        </li>
      </ul>
      <div
        className={`toggle-switch ${isActive ? 'active' : ''}`}
        onClick={toggleSwitch}
      >
        <div className='toggle-switch-ball'></div>
      </div>
    </nav>
  );
};

export default Navbar;
