import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';
import exerciseUtils from '../utils/exercise.js';

function workoutReducer(state = initialState.workouts, action) {
  console.log('Action Type:', action.type);
  console.log('State Before:', state);
  console.log('Action Payload:', action.payload);

  switch (action.type) {
    // Workout Reducers

    case actionTypes.ADD_WORKOUT:
      const { programIdForAddWorkout, workoutForAddWorkout } = action.payload;
      return {
        ...state,
        programs: {
          ...state.programs,
          [programIdForAddWorkout]: {
            ...state.programs[programIdForAddWorkout],
            workouts: {
              ...state.programs[programIdForAddWorkout].workouts,
              [workoutForAddWorkout.id]: workoutForAddWorkout
            }
          }
        }
      };

    case actionTypes.UPDATE_WORKOUT:
      const { programIdForUpdateWorkout, workoutForUpdateWorkout } =
        action.payload;
      console.log('Updating workout in reducer:', workoutForUpdateWorkout);
      return {
        ...state,
        programs: {
          ...state.programs,
          [programIdForUpdateWorkout]: {
            ...state.programs[programIdForUpdateWorkout],
            workouts: {
              ...state.programs[programIdForUpdateWorkout].workouts,
              [workoutForUpdateWorkout.id]: {
                ...state.programs[programIdForUpdateWorkout].workouts[
                  workoutForUpdateWorkout.id
                ],
                ...workoutForUpdateWorkout
              }
            }
          }
        }
      };

    case actionTypes.DELETE_WORKOUT:
      const { programIdForDeleteWorkout, workoutIdForDeleteWorkout } =
        action.payload;
      if (!programIdForDeleteWorkout || !workoutIdForDeleteWorkout) {
        console.error('Invalid payload for DELETE_WORKOUT', action.payload);
        return state;
      }

      const {
        [workoutIdForDeleteWorkout]: deletedWorkout,
        ...remainingWorkouts
      } = state.programs[programIdForDeleteWorkout].workouts;

      const reorderedWorkouts = Object.values(remainingWorkouts)
        .sort((a, b) => a.order - b.order)
        .map((workout, index) => ({
          ...workout,
          order: index + 1
        }))
        .reduce((acc, workout) => {
          acc[workout.id] = workout;
          return acc;
        }, {});

      return {
        ...state,
        programs: {
          ...state.programs,
          [programIdForDeleteWorkout]: {
            ...state.programs[programIdForDeleteWorkout],
            workouts: reorderedWorkouts
          }
        }
      };

    // Exercise Reducers

    // Exercise Reducers
    case actionTypes.ADD_EXERCISE:
      const { workoutId: workoutIdAddEx, exercises } = action.payload;
      const existingWorkout =
        state.programs[state.activeProgramId].workouts[workoutIdAddEx];
      if (!existingWorkout) {
        console.error(`Workout with id ${workoutIdAddEx} not found`);
        return state;
      }

      const existingExerciseIds = new Set(
        existingWorkout.exercises.map(ex => ex.id || ex.tempId)
      );
      const newExercises = exercises.filter(
        ex => !existingExerciseIds.has(ex.id || ex.tempId)
      );

      const updatedExercisesAfterAdd = [
        ...existingWorkout.exercises,
        ...newExercises.map(ex => createNewExercise(ex))
      ];

      return {
        ...state,
        programs: {
          ...state.programs,
          [state.activeProgramId]: {
            ...state.programs[state.activeProgramId],
            workouts: {
              ...state.programs[state.activeProgramId].workouts,
              [workoutIdAddEx]: {
                ...existingWorkout,
                exercises: updatedExercisesAfterAdd
              }
            }
          }
        }
      };

    case actionTypes.TOGGLE_EXERCISE_SELECTION:
      const { exerciseIdForToggle, exerciseData } = action.payload;
      const activeWorkout =
        state.programs[state.activeProgramId].workouts[state.activeWorkoutId];

      const existingExerciseIndex = activeWorkout.exercises.findIndex(
        ex => (ex.id || ex.tempId) === exerciseIdForToggle
      );

      let updatedExercises;
      if (existingExerciseIndex === -1) {
        // Exercise doesn't exist, add it
        updatedExercises = [
          ...activeWorkout.exercises,
          createNewExercise({
            ...exerciseData,
            order: activeWorkout.exercises.length + 1,
            selected: true
          })
        ];
      } else {
        // Exercise exists, remove it
        updatedExercises = activeWorkout.exercises.filter(
          (_, index) => index !== existingExerciseIndex
        );
      }

      return {
        ...state,
        programs: {
          ...state.programs,
          [state.activeProgramId]: {
            ...state.programs[state.activeProgramId],
            workouts: {
              ...state.programs[state.activeProgramId].workouts,
              [state.activeWorkoutId]: {
                ...activeWorkout,
                exercises: updatedExercises
              }
            }
          }
        }
      };

    case actionTypes.REMOVE_EXERCISE:
      const { workoutId: wId5, exerciseId: exId5 } = action.payload;
      const workout5 = state.programs[state.activeProgramId].workouts[wId5];
      if (!workout5) return state;

      const updatedExercisesAfterRemove = workout5.exercises.filter(
        exercise => (exercise.id || exercise.tempId) !== exId5
      );

      return {
        ...state,
        programs: {
          ...state.programs,
          [state.activeProgramId]: {
            ...state.programs[state.activeProgramId],
            workouts: {
              ...state.programs[state.activeProgramId].workouts,
              [wId5]: {
                ...workout5,
                exercises: updatedExercisesAfterRemove
              }
            }
          }
        }
      };

    // Set Reducers

    case actionTypes.ADD_SET:
      const {
        workoutId: workoutIdAddSet,
        exerciseId,
        weight,
        reps
      } = action.payload;
      const workoutToUpdate = state[workoutIdAddSet];
      if (!workoutToUpdate) return state;

      return {
        ...state,
        [workoutIdAddSet]: {
          ...workoutToUpdate,
          exercises: workoutToUpdate.exercises.map(exercise =>
            exerciseUtils.getExerciseId(exercise) === exerciseId
              ? {
                  ...exercise,
                  sets: [
                    ...exercise.sets,
                    {
                      id: uuidv4(),
                      weight,
                      reps,
                      order: exercise.sets.length + 1
                    }
                  ]
                }
              : exercise
          )
        }
      };

    case actionTypes.UPDATE_SET:
      const { workoutId: wId2, exerciseId: exId, updatedSet } = action.payload;
      const workout2 = state[wId2];
      if (!workout2) return state;

      const updatedExercisesAfterSetUpdate = workout2.exercises.map(
        exercise => {
          if (exercise.id === exId) {
            return {
              ...exercise,
              sets: exercise.sets.map(set =>
                set.id === updatedSet.id ? { ...set, ...updatedSet } : set
              )
            };
          }
          return exercise;
        }
      );

      return {
        ...state,
        [wId2]: {
          ...workout2,
          exercises: updatedExercisesAfterSetUpdate
        }
      };

    case actionTypes.REMOVE_SET:
      const {
        workoutId: workoutIdDeleteSet,
        exerciseId: exerciseIdDeleteSet,
        setId: setIdDeleteSet
      } = action.payload;
      const workoutDeleteSet = state[workoutIdDeleteSet];
      if (!workoutDeleteSet) return state;

      const updatedExercisesDeleteSet = workoutDeleteSet.exercises.map(
        exercise => {
          const exerciseIdToCompare = exerciseUtils.getExerciseId(exercise);
          if (exerciseIdToCompare === exerciseIdDeleteSet) {
            return {
              ...exercise,
              sets: exercise.sets.filter(set => set.id !== setIdDeleteSet)
            };
          }
          return exercise;
        }
      );

      return {
        ...state,
        [workoutIdDeleteSet]: {
          ...workoutDeleteSet,
          exercises: updatedExercisesDeleteSet
        }
      };

    default:
      return state;
  }
}

export { workoutReducer };
