import { initialState } from './initialState';
import { actionTypes } from '../actions/actionTypes';

function programReducer(state = initialState.programs, action) {
  switch (action.type) {
    case actionTypes.ADD_PROGRAM:
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;
      return {
        ...state,
        [id]: {
          ...state[id],
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };

    case actionTypes.SET_PROGRAM:
      return {
        [action.payload.id]: action.payload
      };

    case actionTypes.DELETE_PROGRAM:
      const { programId } = action.payload;
      const { [programId]: _, ...remainingPrograms } = state;
      return remainingPrograms;

    default:
      return state;
  }
}

export { programReducer };
