import { initialState } from './initialState';

function programReducer(state = initialState.programs, action) {
  switch (action.type) {
    case 'RESET_STATE':
      return action.payload;

    case 'SET_SELECTED_PROGRAM':
      const newState = {
        ...state,
        selectedProgram: action.payload
      };

      return newState;

    case 'ADD_PROGRAM':
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      if (!state[id]) {
        console.error('Program not found:', id);
        return state; // Return the current state if the program ID does not exist.
      }

      const updatedState = {
        ...state,
        [id]: {
          ...state[id], // Spread the existing program details
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };

      return updatedState;

    case 'UPDATE_PROGRAM': {
      const updatedProgram = action.payload;

      return {
        ...state,
        selectedProgram: updatedProgram,
        [updatedProgram.id]: updatedProgram
      };
    }

    case 'UPDATE_PROGRAM_SUCCESS': {
      const updatedProgram = action.payload;

      if (!updatedProgram || !updatedProgram.id) {
        console.error('Invalid program data received:', updatedProgram);
        return state;
      }

      const updatedState = {
        ...state,
        [updatedProgram.id]: updatedProgram,
        selectedProgram: updatedProgram
      };

      return updatedState;
    }

    case 'UPDATE_PROGRAM_WORKOUT':
      if (!state.selectedProgram) {
        console.warn('No selected program found when updating workout');
        return state;
      }
      return {
        ...state,
        selectedProgram: {
          ...state.selectedProgram,
          workouts: state.selectedProgram.workouts
            ? state.selectedProgram.workouts.map(workout =>
                workout.id === action.payload.id ? action.payload : workout
              )
            : [action.payload]
        }
      };

    case 'DELETE_PROGRAM': {
      const { programId } = action.payload;

      if (!programId) {
        console.error('Invalid payload for DELETE_PROGRAM', action.payload);
        return state;
      }

      const newPrograms = { ...state.programs };
      delete newPrograms[programId];

      const newState = {
        ...state,
        programs: newPrograms
      };

      return newState;
    }

    default:
      return state;
  }
}

export { programReducer };
