import React, { useState } from 'react';
import PropTypes from 'prop-types';
import {
  TbLayoutNavbarExpandFilled,
  TbLayoutBottombarExpandFilled
} from 'react-icons/tb';
import { MdRemoveCircle } from 'react-icons/md';
import Button from '../Inputs/Button';
import './DayContainer.css';

const DayContainer = ({ day, onAddExercise, handleRemoveDay }) => {
  const [exercises, setExercises] = useState([]);
  const [isExpanded, setIsExpanded] = useState(day.id === 1);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div>
      <div className='day-container'>
        <div className='day-container__header' onClick={toggleExpand}>
          <button
            id='expand-day-card-button'
            onClick={toggleExpand}
            title='Expand/Collapse Day'
          >
            {isExpanded ? (
              <TbLayoutBottombarExpandFilled size={30} />
            ) : (
              <TbLayoutNavbarExpandFilled size={30} />
            )}
          </button>
          <h2 id='day-container__title'>{day.name}</h2>
          <button
            id='delete-day-card-button'
            onClick={() => handleRemoveDay(day.id)}
            title='Remove Day'
          >
            <MdRemoveCircle size={30} />
          </button>
        </div>
        {isExpanded && (
          <div className='day-body'>
            <div className='day-body-header'>
              <h3>Exercises</h3>
              <Button
                id='save-program-button'
                onClick={() => onAddExercise(day.id)}
                type='button'
                bgColor='#EAEAEA'
              >
                Add Exercise
              </Button>
            </div>
            <div className='day-body-content'>
              {exercises.map(exercise => (
                <div key={exercise.id} className='exercise'>
                  <h4>{exercise.name}</h4>
                  <button
                    onClick={() => {
                      setExercises(
                        exercises.filter(ex => ex.id !== exercise.id)
                      );
                    }}
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

DayContainer.propTypes = {
  day: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired
  }).isRequired,
  onAddExercise: PropTypes.func.isRequired,
  onRemove: PropTypes.func.isRequired
};

export default DayContainer;
