import React, { useState, useEffect } from 'react';
import Dropdown from './Dropdown'; // Import your Dropdown component

function CategoryDropdown() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Any Category');
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    // Fetch categories from the API
    fetch('http://localhost:9025/api/equipment-categories')
      .then(response => response.json())
      .then(data => setCategories(data))
      .catch(error => console.error('Failed to fetch categories:', error));
  }, []);

  const toggleDropdown = () => setIsOpen(!isOpen);

  return (
    <div>
      <button onClick={toggleDropdown} className='category-select-button'>
        {selectedCategory}
      </button>
      {isOpen && (
        <Dropdown
          options={categories}
          selectedOption={selectedCategory}
          onOptionSelect={setSelectedCategory}
        />
      )}
    </div>
  );
}

export default CategoryDropdown;
