import React, { useState, useEffect } from 'react';
import Exercise from '../../components/Exercise/Exercise';
import SearchBar from '../../components/SearchBar/SearchBar';
import Autocomplete from '@mui/material/Autocomplete';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import './ExercisesListPage.css';

const ExercisesListPage = () => {
  const [exercises, setExercises] = useState([]);
  const [equipments, setEquipments] = useState([]);
  const [muscles, setMuscles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMuscle, setSelectedMuscle] = useState('');
  const [selectedEquipment, setSelectedEquipment] = useState('');
  const [filteredExercises, setFilteredExercises] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch exercises catalog
        const exercisesResponse = await fetch(
          'http://localhost:9025/api/exercise-catalog'
        );
        const exercisesCatalog = await exercisesResponse.json();
        setExercises(exercisesCatalog);
        setFilteredExercises(exercisesCatalog);

        // Fetch equipment catalog
        const equipmentResponse = await fetch(
          'http://localhost:9025/api/equipments'
        );
        const equipmentCatalog = await equipmentResponse.json();
        setEquipments(equipmentCatalog);

        // Fetch muscle catalog
        const musclesResponse = await fetch(
          'http://localhost:9025/api/muscles'
        );
        const musclesCatalog = await musclesResponse.json();
        setMuscles(musclesCatalog);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };
    fetchData();
  }, []); // Empty dependency array means this runs once on mount

  const handleSearch = value => {
    setSearchTerm(value);
  };

  const handleMuscleChange = value => {
    setSelectedMuscle(value);
  };

  const handleEquipmentChange = value => {
    setSelectedEquipment(value);
  };

  return (
    <div className='page-layout'>
      <h1 className='page-title'>Exercises</h1>

      <SearchBar onChange={handleSearch} />
      <ExerciseFilters
        onMuscleChange={handleMuscleChange}
        onEquipmentChange={handleEquipmentChange}
      />
      <div className='exercise-list'>
        {filteredExercises.map(exercise => (
          <Exercise
            key={exercise.exercise_id}
            name={exercise.name}
            muscle={exercise.muscle}
            equipment={exercise.equipment}
            image={`http://localhost:9025/${exercise.file_path}`}
          />
        ))}
      </div>
    </div>
  );
};

export default ExercisesListPage;
