import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Button from '../../../components/Inputs/Button';
import NavBar from '../../../components/Nav/Nav';
import { ProgramContext } from '../../../contexts/programContext';
import { useTheme } from '../../../contexts/themeContext';
import './programs.css';

const ProgramDetailsPage = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [localPrograms, setLocalPrograms] = useState([]);

  const { state, deleteProgram } = useContext(ProgramContext);
  const { theme } = useTheme();

  const handleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const fetchPrograms = async () => {
    try {
      const response = await fetch('http://localhost:9025/api/programs/2');
      const data = await response.json();
      setLocalPrograms(data || []);
    } catch (error) {
      console.error('Error fetching programs:', error);
    }
  };

  useEffect(() => {
    // Fetch all programs initially
    fetchPrograms();
  }, []);

  useEffect(() => {
    if (state.programs) {
      setLocalPrograms(Object.values(state.programs));
    }
  }, [state.programs]);

  const handleDeleteProgram = async programId => {
    await deleteProgram(programId);
    // Re-fetch programs after deletion
    fetchPrograms();
  };

  return (
    <div>
      <NavBar />
      <div className='view-prog-page'>
        <div className='view-prog-page__page-title-container'>
          <h1 className='view-prog-page__page-title'>Program Details</h1>
          <Link
            className={`view-prog-page__title-link ${theme}`}
            to='/programs'
          >
            Back to Programs
          </Link>
        </div>
      </div>
      <div className='view-prog-page__program-list'></div>
      <div className='create-prog-page__button-container'>
        <Button type='button' onClick={console.log('edit program')}>
          Edit
        </Button>
        <Button type='submit' onClick={handleDeleteProgram}>
          Delete
        </Button>
      </div>
    </div>
  );
};

export default ProgramDetailsPage;
