import { createContext, useReducer } from 'react';
import { actionTypes } from '../actions/actionTypes';
import rootReducer from '../reducers/rootReducer';
import { initialState } from '../reducers/initialState';
import { standardizeWorkout } from '../utils/standardizeWorkout';
import exerciseUtils from '../utils/exercise.js';
import { v4 as uuidv4 } from 'uuid';

export const ProgramContext = createContext();

export const ProgramProvider = ({ children }) => {
  const [state, dispatch] = useReducer(rootReducer, initialState);

  const setSelectedProgram = programId => {
    dispatch({
      type: actionTypes.SET_SELECTED_PROGRAM,
      payload: programId
    });
  };

  const setActiveWorkout = workoutId => {
    dispatch({
      type: actionTypes.SET_ACTIVE_WORKOUT,
      payload: workoutId
    });
  };

  const saveProgram = async () => {
    const newProgram = {
      ...state.program,
      workouts: state.program.workouts.map(workout => ({
        ...workout,
        exercises: workout.exercises.map(exercise => ({
          catalog_exercise_id: exercise.catalog_exercise_id,
          order: exercise.order || 1,
          sets: exercise.sets.map((set, index) => ({
            ...set,
            order: index + 1
          }))
        })),
        order: workout.order || 1
      }))
    };

    dispatch({ type: actionTypes.SAVE_PROGRAM_START });
    try {
      validateProgramData(newProgram); // Validate data before sending
      const response = await fetch('http://localhost:9025/api/programs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newProgram)
      });

      if (!response.ok) {
        const errorText = await response.text(); // Get the response text
        console.error('Error saving program:', errorText); // Log the error text
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();
      dispatch({
        type: actionTypes.SAVE_PROGRAM_SUCCESS,
        payload: savedProgram
      });
    } catch (error) {
      console.error('Failed to save program:', error);
      dispatch({
        type: actionTypes.SAVE_PROGRAM_FAILURE,
        payload: error.message
      });
    }
  };

  const updateProgram = async updatedProgram => {
    dispatch({ type: actionTypes.SAVE_PROGRAM_START });
    try {
      validateProgramData(updatedProgram);

      const response = await fetch(
        `http://localhost:9025/api/programs/${updatedProgram.id}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(updatedProgram)
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error updating program:', errorText);
        throw new Error('Network response was not ok');
      }
      const savedProgram = await response.json();

      dispatch({
        type: actionTypes.UPDATE_PROGRAM_SUCCESS,
        payload: savedProgram
      });

      return savedProgram; // Return the saved program
    } catch (error) {
      console.error('Failed to update program:', error);
      dispatch({
        type: actionTypes.SAVE_PROGRAM_FAILURE,
        payload: error.message
      });
    }
  };

  const updateWorkoutAndProgram = updatedWorkout => {
    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: updatedWorkout
    });
    dispatch({
      type: actionTypes.UPDATE_PROGRAM_WORKOUTS,
      payload: {
        programId: updatedWorkout.programId,
        workout: updatedWorkout
      }
    });
  };

  const validateProgramData = programData => {
    if (!programData.workouts || !Array.isArray(programData.workouts)) {
      throw new Error('Workouts should be an array.');
    }
    programData.workouts.forEach(workout => {
      if (!workout.exercises || !Array.isArray(workout.exercises)) {
        throw new Error('Exercises should be an array.');
      }
      workout.exercises.forEach(exercise => {
        if (!exercise.sets || !Array.isArray(exercise.sets)) {
          throw new Error('Sets should be an array.');
        }
      });
    });
  };

  const addProgram = details => {
    dispatch({
      type: 'ADD_PROGRAM',
      payload: {
        ...details,
        workouts: [] // Ensure workouts array is initialized
      }
    });
  };

  const deleteProgram = async programId => {
    try {
      const response = await fetch(
        `http://localhost:9025/api/programs/${programId}`,
        {
          method: 'DELETE'
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error deleting program:', errorText);
        throw new Error('Failed to delete program');
      }

      dispatch({
        type: actionTypes.DELETE_PROGRAM,
        payload: { programId }
      });
    } catch (error) {
      console.error('Failed to delete program:', error);
    }
  };

  const addWorkout = () => {
    const programId = state.selectedProgramId;
    const workoutsForProgram = Object.values(state.workouts).filter(
      w => w.programId === programId
    );

    // Determine the highest current order and add 1 for the new workout
    const newOrder =
      workoutsForProgram.length > 0
        ? Math.max(...workoutsForProgram.map(w => w.order)) + 1
        : 1;

    const newWorkout = {
      id: uuidv4(),
      name: 'New Workout',
      programId: programId,
      selected: true,
      exercises: [],
      order: newOrder
    };

    dispatch({ type: actionTypes.ADD_WORKOUT, payload: newWorkout });

    // Update the program's workouts array
    dispatch({
      type: actionTypes.UPDATE_PROGRAM_WORKOUTS,
      payload: { programId, workout: newWorkout }
    });

    console.log('addWorkout newWorkout', newWorkout);
  };

  const updateWorkout = workout => {
    const standardizedWorkout = standardizeWorkout(workout);
    if (!standardizedWorkout) {
      console.error('Invalid workout object:', workout);
      return;
    }

    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: standardizedWorkout
    });
  };

  const deleteWorkout = workoutId => {
    dispatch({
      type: actionTypes.DELETE_WORKOUT,
      payload: workoutId
    });
  };

  const addExercise = (workoutId, exercises) => {
    const standardizedExercises = exercises.map(ex => ({
      ...ex,
      tempId: ex.tempId || uuidv4(),
      catalog_exercise_id: ex.catalog_exercise_id || ex.id,
      sets: ex.sets || []
    }));

    // Remove duplicates based on catalog_exercise_id
    const uniqueExercises = Array.from(
      new Set(standardizedExercises.map(ex => ex.catalog_exercise_id))
    ).map(id =>
      standardizedExercises.find(ex => ex.catalog_exercise_id === id)
    );

    dispatch({
      type: actionTypes.ADD_EXERCISE,
      payload: { workoutId, exercises: uniqueExercises }
    });

    // Update the program's workout
    dispatch({
      type: actionTypes.UPDATE_PROGRAM_WORKOUT,
      payload: { workoutId, exercises: uniqueExercises }
    });
  };

  const updateExercise = (workoutId, exercise) => {
    dispatch({
      type: actionTypes.UPDATE_EXERCISE,
      payload: { workoutId, exercise }
    });
  };

  const removeExercise = (workoutId, exerciseId) => {
    const workout = state.workouts[workoutId];
    const updatedExercises = workout.exercises
      .filter(ex => ex.id !== exerciseId)
      .map((exercise, index) => ({ ...exercise, order: index + 1 }));

    dispatch({
      type: actionTypes.REMOVE_EXERCISE,
      payload: { workoutId, exerciseId, updatedExercises }
    });
  };

  const addSet = (workoutId, exerciseId, weight = 10, reps = 10) => {
    const workout = state.workouts[workoutId];

    if (!workout) {
      return;
    }

    const updatedExercises = workout.exercises.map(ex => {
      if (exerciseUtils.getExerciseId(ex) === exerciseId) {
        const updatedSets = [
          ...ex.sets,
          { id: uuidv4(), weight, reps, order: ex.sets.length + 1 }
        ].map((set, index) => ({ ...set, order: index + 1 }));
        return { ...ex, sets: updatedSets };
      }
      return ex;
    });

    dispatch({
      type: actionTypes.ADD_SET,
      payload: { workoutId, exerciseId, weight, reps, updatedExercises }
    });
  };

  const updateSet = (workoutId, exerciseId, updatedSet) => {
    dispatch({
      type: actionTypes.UPDATE_SET,
      payload: { workoutId, exerciseId, updatedSet }
    });
  };

  const removeSet = (workoutId, exerciseId, setId) => {
    const workout = state.workouts[workoutId];
    if (!workout) {
      console.error('Workout not found:', workoutId);
      return;
    }

    const exercise = workout.exercises.find(
      ex => exerciseUtils.getExerciseId(ex) === exerciseId
    );
    if (!exercise) {
      console.error(
        'Exercise not found:',
        exerciseId,
        'in workout:',
        workoutId
      );
      return;
    }

    const updatedSets = exercise.sets.filter(set => set.id !== setId);
    if (updatedSets.length === exercise.sets.length) {
      console.error('Set not found:', setId, 'in exercise:', exerciseId);
      return;
    }

    const updatedExercises = workout.exercises.map(ex => {
      if (exerciseUtils.getExerciseId(ex) === exerciseId) {
        const updatedSets = ex.sets
          .filter(set => set.id !== setId)
          .map((set, index) => ({ ...set, order: index + 1 }));
        return { ...ex, sets: updatedSets };
      }
      return ex;
    });

    dispatch({
      type: actionTypes.UPDATE_WORKOUT,
      payload: {
        ...workout,
        exercises: updatedExercises
      }
    });
  };

  const clearState = () => {
    dispatch({ type: actionTypes.CLEAR_STATE });
  };

  return (
    <ProgramContext.Provider
      value={{
        state,
        dispatch,
        setSelectedProgram,
        addProgram,
        updateProgram,
        updateWorkoutAndProgram,
        deleteProgram,
        addWorkout,
        updateWorkout,
        deleteWorkout,
        setActiveWorkout,
        addExercise,
        updateExercise,
        removeExercise,
        addSet,
        updateSet,
        removeSet,
        saveProgram,
        clearState
      }}
    >
      {children}
    </ProgramContext.Provider>
  );
};
