import React, { useContext, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { ProgramContext } from '../../../contexts/programContext';
import { useTheme } from '../../../contexts/themeContext';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';
import Button from '../../../components/Inputs/Button';
import './program.css';

const CreateProgram = () => {
  const { state, saveProgram, dispatch, setActiveWorkout, clearState } =
    useContext(ProgramContext);

  const navigate = useNavigate();
  const location = useLocation();
  const { theme } = useTheme();

  useEffect(() => {
    // console.log('Received state in ProgramPage:', location.state);
    if (location.state && location.state.activeWorkout) {
      setActiveWorkout('workout', location.state.activeWorkout);
    }
  }, [location.state, setActiveWorkout]);

  useEffect(() => {
    console.log('State changed:', state);
  }, [state]);

  const handleExpandProgram = () => {
    if (
      state.activeElement.type === 'program' &&
      state.activeElement.id === 'program'
    ) {
      setActiveWorkout(null, null);
    } else {
      setActiveWorkout('program', 'program');
    }
  };

  const handleExpandWorkout = workoutId => {
    if (
      state.activeElement.type === 'workout' &&
      state.activeElement.id === workoutId
    ) {
      setActiveWorkout(null, null);
    } else {
      setActiveWorkout('workout', workoutId);
    }
  };

  const handleSaveProgram = async () => {
    // console.log(
    //   'Saving program:',
    //   state.programs[Object.keys(state.programs)[0]]
    // );
    try {
      await saveProgram(state.programs[Object.keys(state.programs)[0]]);
      navigate('/programs');
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    const currentProgramId = Object.keys(state.programs)[0];
    // console.log('currentProgramId:', currentProgramId);
    // console.log('handleAddWorkout called from CreateProgram.js');
    event.preventDefault();
    dispatch({
      type: 'ADD_WORKOUT',
      payload: { programId: currentProgramId }
    });
  };

  if (!state || !state.programs || Object.keys(state.programs).length === 0) {
    return <div>Loading or no programs available...</div>;
  }

  //console.log('State:', state); // Log the state

  return (
    <div>
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={state.programs[Object.keys(state.programs)[0]]}
              isEditing={true}
              isExpanded={
                state.activeElement.type === 'program' &&
                state.activeElement.id === 'program'
              }
              onToggleExpand={handleExpandProgram}
            />
            {state.workouts && Object.keys(state.workouts).length > 0 ? (
              Object.values(state.workouts).map(workout => {
                if (!workout || !workout.id) {
                  console.error('Invalid workout object:', workout); // Log invalid workout object
                  return null;
                }
                return (
                  <Workout
                    key={workout.id}
                    workout={workout}
                    isExpanded={
                      state.activeElement.type === 'workout' &&
                      state.activeElement.id === workout.id
                    }
                    onToggleExpand={() => handleExpandWorkout(workout.id)}
                  />
                );
              })
            ) : (
              <div className={`create-prog-page__no-workout ${theme}`}>
                No workouts
              </div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleSaveProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateProgram;
