import { v4 as uuidv4 } from 'uuid';
import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function workoutReducer(state = initialState.workouts, action) {
  console.log('Action Type:', action.type);
  console.log('State Before:', state);

  switch (action.type) {
    case actionTypes.ADD_WORKOUT: {
      const workoutId = uuidv4();
      const workoutTitle =
        action.payload.name || `Workout ${Object.keys(state).length + 1}`;
      const newWorkout = {
        id: workoutId,
        name: workoutTitle,
        exercises: [],
        active: false, // New workouts are not active by default
        programId: action.payload.programId
      };

      const newState = {
        ...state,
        [workoutId]: newWorkout
      };
      console.log('State After ADD_WORKOUT:', newState);
      return newState;
    }

    case actionTypes.UPDATE_WORKOUT: {
      const newState = {
        ...state,
        [action.payload.id]: {
          ...state[action.payload.id],
          ...action.payload
        }
      };
      console.log('State After UPDATE_WORKOUT:', newState);
      return newState;
    }

    case actionTypes.DELETE_WORKOUT: {
      const newWorkouts = { ...state };
      delete newWorkouts[action.payload];
      console.log('State After DELETE_WORKOUT:', newWorkouts);
      return newWorkouts;
    }

    case actionTypes.SET_ACTIVE_WORKOUT: {
      const { workoutId } = action.payload;
      const newState = Object.fromEntries(
        Object.entries(state).map(([id, workout]) => [
          id,
          { ...workout, active: id === workoutId }
        ])
      );
      console.log('State After SET_ACTIVE_WORKOUT:', newState);
      return newState;
    }

    default:
      console.log('State After Default:', state);
      return state;
  }
}

export { workoutReducer };
