import { actionTypes } from '../actions/actionTypes';

function exerciseReducer(state = {}, action) {
  console.log('Action Type:', action.type);
  console.log('State Before:', state);

  switch (action.type) {
    case actionTypes.ADD_EXERCISE: {
      console.log('ADD_EXERCISE action triggered', action.payload);
      const { workoutId, exercises = [] } = action.payload;
      const currentExercises = state[workoutId] || [];
      console.log('currentExercises:', currentExercises);

      // Use a Set to avoid duplicate exercises
      const updatedExercises = [
        ...currentExercises,
        ...exercises.filter(
          ex => !currentExercises.some(currEx => currEx.id === ex.id)
        )
      ];

      const newState = {
        ...state,
        [workoutId]: updatedExercises
      };
      console.log('Current state before adding exercise:', newState);
      return newState;
    }

    case actionTypes.UPDATE_EXERCISE: {
      console.log('Current state before adding exercise:', state);
      const { workoutId, updatedExercise } = action.payload;
      return {
        ...state,
        [workoutId]: state[workoutId].map(exercise =>
          exercise.id === updatedExercise.id
            ? {
                ...exercise,
                ...updatedExercise,
                // Ensure the exercise ID is preserved
                id: updatedExercise.id
              }
            : exercise
        )
      };
    }

    case actionTypes.DELETE_EXERCISE: {
      if (!action.payload) {
        console.error('Invalid payload for DELETE_EXERCISE', action.payload);
        return state;
      }
      const { workoutId, exerciseId } = action.payload;

      // Filter out the deleted exercise and reorder the remaining exercises
      const updatedExercises = state[workoutId]
        .filter(ex => ex.id !== exerciseId)
        .map((exercise, index) => ({
          ...exercise,
          order: index + 1
        }));

      return {
        ...state,
        [workoutId]: updatedExercises
      };
    }

    default:
      return state;
  }
}

export { exerciseReducer };
