import React, { useState, useEffect } from 'react';
import Dropdown from '../Dropdown/Dropdown';
import './ExerciseFilters.css';

function EquipmentDropdown() {
  const [equipments, setEquipments] = useState([]);
  const [selectedEquipment, setSelectedEquipment] = useState('Any Equipment');
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    // Fetch equipment from the API
    fetch('http://localhost:9025/api/equipments')
      .then(response => response.json())
      .then(data => setEquipments(data))
      .catch(error => console.error('Failed to fetch equipments:', error));
  }, []);
  const toggleDropdown = () => setIsOpen(!isOpen);

  // Make sure to pass only the names of the equipments to the Dropdown options
  const equipmentOptions = equipments.map(
    equipment => equipment.equipment_name
  );

  // Define a method to handle selecting an equipment
  const handleEquipmentSelect = equipmentName => {
    // Find the selected equipment object
    const selected = equipments.find(
      equipment => equipment.equipment_name === equipmentName
    );
    // Update the state with the selected equipment's name
    setSelectedEquipment(selected.equipment_name);
    // You might want to do something with the selected equipment's id as well
    // For example, storing it in a state, or performing some action
    setIsOpen(false); // Close the dropdown
  };

  const buttonClass =
    selectedEquipment === 'Any Equipment' ? 'button-grey' : 'button-orange';

  return (
    <div>
      <button
        onClick={toggleDropdown}
        className={`equipment-select-button ${buttonClass}`}
      >
        {selectedEquipment}
      </button>
      {isOpen && (
        <Dropdown
          options={equipmentOptions}
          selectedOption={selectedEquipment}
          onOptionSelect={handleEquipmentSelect}
        />
      )}
    </div>
  );
}

export default EquipmentDropdown;
