import { actionTypes } from '../actions/actionTypes';

export function workoutReducer(state = {}, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      return {
        ...state,
        [action.payload.workoutId]: {
          ...state[action.payload.workoutId],
          exercises: [
            ...(state[action.payload.workoutId]?.exercises || []),
            ...action.payload.exercises
          ]
        }
      };
    case actionTypes.REMOVE_EXERCISE:
      return {
        ...state,
        [action.payload.workoutId]: {
          ...state[action.payload.workoutId],
          exercises: state[action.payload.workoutId].exercises.filter(
            ex => ex.id !== action.payload.exerciseId
          )
        }
      };
    default:
      return state;
  }
}
