import React, { useState, useEffect } from 'react';
import Exercise from '../../components/Exercise/Exercise';
import Stack from '@mui/material/Stack';

const ExercisesList = ({ onSelect, selectMuscle, selectEquipment }) => {
  const [exercises, setExercises] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        // Fetch exercises catalog
        const response = await fetch(
          'http://localhost:9025/api/exercise-catalog'
        );
        const exercisesCatalog = await exercisesResponse.json();
        setExercises(exercisesCatalog);
        setFilteredExercises(exercisesCatalog);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };
    fetchData();
  }, []); // Empty dependency array means this runs once on mount

  return (
    <div className='page-layout'>
      <Stack direction='column' spacing={2}>
        {filteredExercises.map(exercise => (
          <Exercise
            key={exercise.exercise_id}
            name={exercise.name}
            muscle={exercise.muscle}
            equipment={exercise.equipment}
            image={`http://localhost:9025/${exercise.file_path}`}
            onSelect={onSelect}
          />
        ))}
      </Stack>
    </div>
  );
};

export default ExercisesList;
