import React, { useState, useEffect } from 'react';

function Dropdown({ title, options, selectedOption, onOptionSelect }) {
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const closeDropdown = () => {
      setIsOpen(false);
    };

    window.addEventListener('click', closeDropdown);

    return () => {
      window.removeEventListener('click', closeDropdown);
    };
  }, []);
}

function ExerciseFilters() {
  const [muscles, setMuscles] = useState([]);
  const [equipment, setEquipment] = useState([]);
  const [selectedMuscle, setSelectedMuscle] = useState('Select Muscle');
  const [selectedEquipment, setSelectedEquipment] =
    useState('Select Equipment');

  useEffect(() => {
    // Fetch muscles and equipment options from a REST API
    fetch('http://localhost:9025/api/muscles')
      .then(response => response.json())
      .then(data => setMuscles(data))
      .catch(error => console.error('Failed to fetch muscles:', error));

    fetch('http://localhost:9025/api/equipments')
      .then(response => response.json())
      .then(data => setEquipment(data))
      .catch(error => console.error('Failed to fetch equipment:', error));
  }, []);

  return (
    <div>
      <Dropdown
        title='Muscle'
        options={muscles}
        selectedOption={selectedMuscle}
        onOptionSelect={setSelectedMuscle}
      />
      <Dropdown
        title='Equipment'
        options={equipment}
        selectedOption={selectedEquipment}
        onOptionSelect={setSelectedEquipment}
      />
    </div>
  );
}

export default ExerciseFilters;
