import React, { useState, useEffect, useRef } from 'react';
import './homePage.css';

const HomePage = () => {
  const [isContainerInView, setIsContainerInView] = useState(false);
  const containerRef = useRef(null);
  const scrollableRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (containerRef.current && scrollableRef.current) {
        const containerRect = containerRef.current.getBoundingClientRect();
        const scrollableRect = scrollableRef.current.getBoundingClientRect();

        const isInView =
          containerRect.top >= scrollableRect.top &&
          containerRect.bottom <= scrollableRect.bottom;

        setIsContainerInView(isInView);
      }
    };

    const scrollableElement = document.querySelector('.scrollable-div');
    scrollableElement.addEventListener('scroll', handleScroll);

    return () => scrollableElement.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToNextPage = () => {
    window.scrollTo({
      top: window.innerHeight,
      behavior: 'smooth'
    });
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <div className='home-page'>
      <div className='full-viewport-div'>
        <h1 className='app-title'>App Title</h1>
        <div className='arrow scroll-down' onClick={scrollToNextPage}>
          &#8595;
        </div>
      </div>

      <div className='container scrollable-div' ref={scrollableRef}>
        {isContainerInView && (
          <div className='arrow scroll-up' onClick={scrollToTop}>
            &#8593;
          </div>
        )}
        <div
          ref={containerRef}
          className={`expandable-div left ${
            isContainerInView ? 'expanded' : ''
          }`}
        >
          Expandable div
        </div>
        <div
          className={`expandable-div center ${
            isContainerInView ? 'expanded' : ''
          }`}
        >
          Expandable div
        </div>
        <div
          className={`expandable-div right ${
            isContainerInView ? 'expanded' : ''
          }`}
        >
          Expandable div
        </div>
      </div>
    </div>
  );
};

export default HomePage;
