import React, { useState, useMemo, useContext, useEffect } from 'react';
import { ProgramContext } from '../../contexts/programContext';
import NavBar from '../../components/Nav/Nav';
import { useNavigate } from 'react-router-dom';
import { BsChevronCompactLeft } from 'react-icons/bs';
import { useTheme } from '../../contexts/themeContext';
import ExerciseSearch from '../../components/Exercise/Search';
import Exercise from '../../components/Exercise/Exercise';
import useFetchData from '../../hooks/useFetchData';
import './select-exercises.css';

const SelectExercisesPage = () => {
  const { state, toggleExerciseSelection } = useContext(ProgramContext);

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMuscle, setSelectedMuscle] = useState('');
  const [selectedEquipment, setSelectedEquipment] = useState('');
  const navigate = useNavigate();
  const { theme } = useTheme();

  useEffect(() => {
    if (state.workout.activeWorkout) {
      navigate('/select-exercises', {
        state: {
          workoutId: state.workout.activeWorkout,
          selectedExercises:
            state.workout.workouts.find(
              workout => workout.id === state.workout.activeWorkout
            )?.exercises || [],
          isNewProgram
        }
      });
    }
  }, [state.workout.activeWorkout]);

  const program = state.program;
  console.log('program', program);
  const activeWorkout = state.workout.activeWorkout;
  console.log('activeWorkout', activeWorkout);
  const workouts = state.workout.workouts;
  console.log('workouts', workouts);

  const {
    data: exercises,
    isLoading,
    error
  } = useFetchData('http://localhost:9025/api/exercise-catalog');

  const filteredExercises = useMemo(() => {
    return exercises.filter(exercise => {
      const matchesMuscle =
        !selectedMuscle ||
        selectedMuscle === 'All' ||
        exercise.muscle === selectedMuscle;
      const matchesEquipment =
        !selectedEquipment ||
        selectedEquipment === 'All' ||
        exercise.equipment === selectedEquipment;
      const matchesSearchTerm =
        !searchTerm ||
        exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
      return matchesMuscle && matchesEquipment && matchesSearchTerm;
    });
  }, [searchTerm, selectedMuscle, selectedEquipment, exercises]);

  const handleSearch = newValue => setSearchTerm(newValue);
  const handleMuscleChange = value => setSelectedMuscle(value);
  const handleEquipmentChange = value => setSelectedEquipment(value);

  const handleToggleExercise = exercise => {
    console.log(
      '57 handleToggleExercise in select-exercise component - Toggling exercise:',
      exercise
    );
    toggleExerciseSelection(exercise.id, {
      name: exercise.name,
      muscle: exercise.muscle,
      equipment: exercise.equipment
    });
  };

  const handleSaveExercises = () => {
    if (!activeWorkout) {
      alert('No active workout selected.');
      return;
    }
    navigate('/create-program');
  };

  const goBack = () => {
    navigate('/create-program');
  };

  const exerciseText = () => {
    const activeWorkoutObj = workouts.find(
      workout => workout.id === activeWorkout
    );
    const selectedExercises =
      activeWorkoutObj?.exercises.filter(ex => ex.selected) || [];
    const count = selectedExercises.length;
    return count === 0
      ? 'No Exercises '
      : count === 1
      ? '1 Exercise '
      : `${count} Exercises `;
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error loading exercises: {error.message}</div>;

  return (
    <div className='select-exercise-page'>
      <NavBar isEditing='true' />
      <div className='select-exercise'>
        <div className={`select-exercise__header ${theme}`}>
          <button className='select-exercise__back-btn' onClick={goBack}>
            <BsChevronCompactLeft
              className={`select-exercise__icon ${theme}`}
              size={30}
            />
          </button>
          <div className='select-exercise__title-container'>
            <h1 className={`select-exercise__title ${theme}`}>
              {`Adding exercises for ${
                workouts.find(workout => workout.id === activeWorkout)?.name ||
                'your selected workout'
              }`}
            </h1>
            <div className='select-exercise__subtitle'>
              <span className={`select-exercise__count ${theme}`}>
                {exerciseText()}
              </span>
            </div>
          </div>
          <button
            onClick={handleSaveExercises}
            className='select-exercise__add-exercise-btn'
          >
            Save
          </button>
        </div>
        <div className='select-exercise__filters'>
          <ExerciseSearch
            onSearchTextChange={handleSearch}
            exercises={exercises}
            onMuscleChange={handleMuscleChange}
            onEquipmentChange={handleEquipmentChange}
          />
        </div>
        <div className='select-exercise__exercises'>
          {filteredExercises.map(exercise => {
            const workoutExercise = workouts
              .find(workout => workout.id === activeWorkout)
              ?.exercises.find(
                ex =>
                  ex.id === exercise.id ||
                  ex.catalog_exercise_id === exercise.id
              );
            const isSelected = workoutExercise?.selected || false;
            return (
              <Exercise
                key={exercise.id}
                name={exercise.name}
                muscle={exercise.muscle}
                equipment={exercise.equipment}
                image={`http://localhost:9025/${exercise.file_path}`}
                isSelected={isSelected}
                onClick={() => handleToggleExercise(exercise)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SelectExercisesPage;
