import React, { createContext, useContext, useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const WorkoutContext = createContext();

export const useWorkout = () => useContext(WorkoutContext);

export const WorkoutProvider = ({ children }) => {
  const location = useLocation();
  const [workout, setWorkout] = useState({
    templateName: '',
    planType: 'General',
    dayType: 'Day of Week',
    difficulty: 'Intermediate',
    selectedExercises: []
  });

  useEffect(() => {
    // Function to reset state
    const resetWorkout = () => {
      setWorkout({
        templateName: '',
        planType: 'General',
        dayType: 'Day of Week',
        difficulty: 'Intermediate',
        selectedExercises: []
      });
    };
    // Paths that should trigger a reset when navigated away from
    const pathsToReset = ['/create-workout', '/create-workout-details'];

    // Check if the current path is in the list and reset if navigating away
    if (!pathsToReset.includes(location.pathname)) {
      resetWorkout();
    }
  }, [location.pathname]); // Depend on pathname to trigger effect
  return (
    <WorkoutContext.Provider value={{ workout, setWorkout }}>
      {children}
    </WorkoutContext.Provider>
  );
};
