import React, { useState, useContext, useEffect, useRef } from 'react';
import { useNavigate, useLocation, Prompt } from 'react-router-dom';
import { ProgramContext } from '../../../contexts/programContext';
import { useTheme } from '../../../contexts/themeContext';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';
import Button from '../../../components/Inputs/Button';
import './program.css';

const CreateProgram = () => {
  const { state, saveProgram, dispatch, setActiveWorkout, clearState } =
    useContext(ProgramContext);
  const [expandedWorkout, setExpandedWorkout] = useState({});

  const navigate = useNavigate();
  const location = useLocation();
  const prevLocationRef = useRef(location.pathname);
  const { theme } = useTheme();

  useEffect(() => {
    console.log('Received state in ProgramPage:', location.state);
    if (location.state && location.state.activeWorkout) {
      setActiveWorkout(location.state.activeWorkout);
      setExpandedWorkout(prevState => ({
        ...prevState,
        [location.state.activeWorkout]: true
      }));
    }
  }, [location.state, setActiveWorkout]);

  useEffect(() => {
    const handleBeforeUnload = event => {
      if (isFormDirty) {
        event.preventDefault();
        event.returnValue = '';
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    const prevLocation = prevLocationRef.current;
    console.log('prevLocation:', prevLocation);
    const currentLocation = location.pathname;
    console.log('currentLocation:', currentLocation);

    if (
      prevLocation === '/create-program' &&
      currentLocation !== '/create-program'
    ) {
      clearState();
    }

    prevLocationRef.current = currentLocation;
  }, [location, clearState]);

  const handleSaveProgram = async () => {
    console.log(
      'Saving program:',
      state.programs[Object.keys(state.programs)[0]]
    );
    try {
      await saveProgram(state.programs[Object.keys(state.programs)[0]]);
      navigate('/programs');
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  const handleExpandWorkout = workoutId => {
    const isCurrentlyExpanded = expandedWorkout[workoutId];

    setExpandedWorkout(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all
        return acc;
      }, {}),
      [workoutId]: !isCurrentlyExpanded
    }));

    if (!isCurrentlyExpanded) {
      setActiveWorkout(workoutId);
    } else {
      setActiveWorkout(null);
    }
  };

  const handleToggleProgramForm = () => {
    setExpandedWorkout(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all workouts
        return acc;
      }, {}),
      program: !prevState.program // toggle the program form
    }));
  };

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    const currentProgramId = Object.keys(state.programs)[0];
    // console.log('currentProgramId:', currentProgramId);
    // console.log('handleAddWorkout called from CreateProgram.js');
    event.preventDefault();
    dispatch({
      type: 'ADD_WORKOUT',
      payload: { programId: currentProgramId }
    });
  };

  if (!state || !state.programs || Object.keys(state.programs).length === 0) {
    return <div>Loading or no programs available...</div>;
  }

  //console.log('State:', state); // Log the state

  return (
    <div>
      <Prompt
        when={true} // Adjust the condition based on your form dirty state
        message='You have unsaved changes, are you sure you want to leave?'
      />
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={state.programs[Object.keys(state.programs)[0]]}
              isEditing={true}
              isExpanded={expandedWorkout['program']}
              onToggleExpand={handleToggleProgramForm}
            />
            {state.workouts && Object.keys(state.workouts).length > 0 ? (
              Object.values(state.workouts).map(workout => {
                if (!workout || !workout.id) {
                  console.error('Invalid workout object:', workout); // Log invalid workout object
                  return null;
                }
                return (
                  <Workout
                    key={workout.id}
                    workout={workout}
                    isExpanded={expandedWorkout[workout.id] || false}
                    onToggleExpand={() => handleExpandWorkout(workout.id)}
                  />
                );
              })
            ) : (
              <div className={`create-prog-page__no-workout ${theme}`}>
                No workouts
              </div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleSaveProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateProgram;
