// src/App.js
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/home/home';
import ExercisesListPage from './pages/exercises/exercises';
import SelectExercisesPage from './pages/exercises/select-exercises';
import ProgramPage from './pages/programs/main/programs';
import CreateProgramPage from './pages/programs/create/program';
import ProgressPage from './pages/progress/main/progress';
import ProfilePage from './pages/profile/main/profile';
import { ProgramProvider } from './contexts/programContext';
import { useTheme } from './contexts/themeContext';

import './App.css';

function CreateProgramWithProvider() {
  return (
    <ProgramProvider>
      <CreateProgramPage />
    </ProgramProvider>
  );
}

function SelectExercisesWithProvider() {
  return (
    <ProgramProvider>
      <SelectExercisesPage />
    </ProgramProvider>
  );
}

const App = () => {
  const { theme } = useTheme();

  useEffect(() => {
    // When the theme changes, update the class on the <html> element
    document.documentElement.className = theme;
  }, [theme]);

  return (
    <ProgramProvider>
      <Router>
        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path='/exercises' element={<ExercisesListPage />} />
          <Route
            path='/select-exercises'
            element={<SelectExercisesWithProvider />}
          />
          <Route path='/programs' element={<ProgramPage />} />
          <Route
            path='/create-program'
            element={<CreateProgramWithProvider />}
          />
          <Route path='/progress' element={<ProgressPage />} />
          <Route path='/profile' element={<ProfilePage />} />
        </Routes>
      </Router>
    </ProgramProvider>
  );
};

export default App;
