import { initialState } from './initialState';

function programReducer(state = initialState.programs, action) {
  switch (action.type) {
    case 'ADD_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      return {
        ...state,
        [id]: {
          id,
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };
    }

    case 'UPDATE_PROGRAM': {
      const {
        id,
        name,
        program_duration,
        duration_unit,
        days_per_week,
        main_goal
      } = action.payload;

      if (!state[id]) {
        console.error('Program not found:', id);
        return state; // Return the current state if the program ID does not exist.
      }

      return {
        ...state,
        [id]: {
          ...state[id],
          name,
          program_duration,
          duration_unit,
          days_per_week,
          main_goal
        }
      };
    }

    case 'DELETE_PROGRAM': {
      const { programId } = action.payload;

      if (!programId) {
        console.error('Invalid payload for DELETE_PROGRAM', action.payload);
        return state;
      }

      const newPrograms = { ...state };
      delete newPrograms[programId];

      return newPrograms;
    }

    default:
      return state;
  }
}

export { programReducer };
