import React, { useState, useContext, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { ProgramContext } from '../../../contexts/programContext';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';
import Button from '../../../components/Inputs/Button';
import './program.css';

const CreateProgram = () => {
  const {
    state,
    dispatch,
    saveProgram,
    addWorkout,
    setActiveWorkout,
    clearState,
    createInitialProgram
  } = useContext(ProgramContext);
  const [expandedWorkouts, setExpandedWorkouts] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  console.log('Component rendering. Current state:', state);

  const initializeProgram = useCallback(async () => {
    console.log(
      'initializeProgram called. Current selectedProgramId:',
      state.selectedProgramId
    );
    if (!state.selectedProgramId) {
      console.log('No selected program. Creating initial program...');
      const initialProgram = createInitialProgram();
      console.log('Initial program created:', initialProgram);
      await dispatch({
        type: 'SET_INITIAL_PROGRAM',
        payload: initialProgram
      });
      console.log('SET_INITIAL_PROGRAM action dispatched');
    } else {
      console.log('Selected program already exists:', state.selectedProgramId);
    }
    setIsLoading(false);
  }, [state.selectedProgramId, createInitialProgram, dispatch]);

  useEffect(() => {
    console.log('Component mounted. Initializing program...');
    initializeProgram();
  }, [initializeProgram]);

  useEffect(() => {
    console.log('State updated:', state);
    console.log('Selected Program ID:', state.selectedProgramId);
    console.log('Programs:', state.programs);
    console.log('Workouts:', state.workouts);
  }, [state]);

  const currentProgram = state.programs[state.selectedProgramId];

  useEffect(() => {
    if (currentProgram) {
      console.log('Current Program Updated:', currentProgram);
    } else {
      console.log('Current Program is undefined');
    }
  }, [currentProgram]);

  const handleSaveProgram = async () => {
    try {
      await saveProgram(currentProgram);
      navigate('/programs');
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  const handleExpandWorkout = workoutId => {
    const isCurrentlyExpanded = expandedWorkouts[workoutId];

    setExpandedWorkouts(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all
        return acc;
      }, {}),
      [workoutId]: !isCurrentlyExpanded
    }));

    if (!isCurrentlyExpanded) {
      setActiveWorkout(workoutId);
    } else {
      setActiveWorkout(null);
    }
  };

  const handleToggleProgramForm = () => {
    setExpandedWorkouts(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all workouts
        return acc;
      }, {}),
      program: !prevState.program // toggle the program form
    }));
  };

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    event.preventDefault();
    addWorkout(state.selectedProgramId);
  };

  if (isLoading) {
    console.log('Component is in loading state');
    return <div>Loading program...</div>;
  }

  if (!currentProgram) {
    console.log('No current program available');
    return <div>No program available. Please try again.</div>;
  }

  return (
    <div>
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={currentProgram}
              isEditing={true}
              isNewProgram={true}
              isExpanded={expandedWorkouts['program']}
              onToggleExpand={handleToggleProgramForm}
            />
            {currentProgram.workouts &&
            Object.keys(currentProgram.workouts).length > 0 ? (
              Object.values(currentProgram.workouts).map(workout => (
                <Workout
                  key={workout.id}
                  workout={workout}
                  isNewProgram={true}
                  isExpanded={expandedWorkouts[workout.id] || false}
                  onToggleExpand={() => handleExpandWorkout(workout.id)}
                />
              ))
            ) : (
              <div>No workouts available</div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleSaveProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateProgram;
