import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function exerciseReducer(state = initialState, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      const { workoutId, exercise } = action.payload;
      console.log('action.payload:', action.payload);
      if (!state[workoutId]) {
        console.error('No workout matches the provided workoutId:', workoutId);
        return state;
      }
      const updatedExercises = [...state[workoutId], exercise];
      return {
        ...state,
        [workoutId]: updatedExercises
      };

    case actionTypes.UPDATE_EXERCISE:
      console.log('UPDATE_EXERCISE action:', action);
      const { id, updates } = action.payload;
      const workoutContainingExercise = Object.values(state.workouts).find(
        workout => workout.exercises.includes(id)
      );

      if (workoutContainingExercise) {
        return {
          ...state,
          workouts: {
            ...state.workouts,
            [workoutContainingExercise.id]: {
              ...workoutContainingExercise,
              exercises: workoutContainingExercise.exercises.map(ex =>
                ex.id === id ? { ...ex, ...updates } : ex
              )
            }
          }
        };
      } else {
        console.error('Exercise id does not exist in state:', id);
      }
      return state;

    case actionTypes.DELETE_EXERCISE:
      console.log('DELETE_EXERCISE action:', action);
      const newState = { ...state };
      delete newState[action.payload];
      return newState;

    default:
      return state;
  }
}

export { exerciseReducer };
