import React, { useState } from 'react';
import './home.css';

const HomePage = () => {
  const [activeDiv, setActiveDiv] = useState(null);
  const scrollToNextPage = () => {
    window.scrollTo({
      top: window.innerHeight,
      behavior: 'smooth'
    });
  };

  const handleClick = divId => {
    setActiveDiv(activeDiv === divId ? null : divId); // Toggle active state
  };

  return (
    <div>
      <div className='full-viewport-div'>
        <h1 className='main-title'>Pr00f 0f W0rk</h1>
        <div className='scroll-down-arrow' onClick={scrollToNextPage}>
          <div className='scroll-down-arrow' onClick={scrollToNextPage}>
            &#x2193; {/* Downwards arrow symbol, or use an icon */}
          </div>
        </div>
      </div>
      <div className='container'>
        <div
          className={`child yellow ${activeDiv === 'div1' ? 'active' : ''}`}
          onClick={() => handleClick('div1')}
        >
          <h2>Programs</h2>
          <h1>Start Program</h1>
        </div>
        <div className={`child green ${activeDiv === 'div1' ? 'active' : ''}`}>
          <h2>Workout</h2>
          <h1>Workout Using Program</h1>
        </div>
        <div class='child blue'>
          <h2>Workout</h2>
          <h1>Quick Workout</h1>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
